SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

-- ----------------------------
-- Table structure for news
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news`  (
  `id` int(0) NOT NULL AUTO_INCREMENT,
  `title` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '新闻标题',
  `article_fragment_for_show` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章部分片段，用于新闻列表显示每个新闻的部分片段',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '新闻正文',
  `column_id` int(0) NULL DEFAULT NULL COMMENT '外键，新闻栏目id，新闻发布时必须选择新闻栏目',
  `img_source` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '图片来源',
  `article_source` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文字来源',
  `news_status` tinyint(0) NOT NULL COMMENT '新闻状态。0-草稿，1-上传成功（备注：属于中转新闻），2-审核中，3-审核失败（备注：属于中转新闻），4-审核成功，待发布，5-已发布，6-撤销发布，7-打回修改（备注：属于中转新闻）',
  `is_carousel` tinyint(0) NOT NULL DEFAULT 0 COMMENT '新闻是否轮播发布，默认false',
  `set_carousel_time` datetime(0) NULL DEFAULT NULL COMMENT '新闻设置轮播的时机。如果很多新闻都设为轮播，则轮播顺序由该字段决定',
  `is_headlines` tinyint(0) NOT NULL DEFAULT 0 COMMENT '新闻是否作为头条，默认false',
  `set_headlines_time` datetime(0) NULL DEFAULT NULL COMMENT '新闻设置为头条的时机。如果很多新闻都设为头条，则谁作为头条由该字段决定',
  `is_image_news` tinyint(0) NOT NULL DEFAULT 0 COMMENT '是否是图片新闻',
  `is_top` tinyint(0) NOT NULL DEFAULT 0 COMMENT '是否置顶。表示是否在所在的新闻栏目置顶',
  `set_top_time` datetime(0) NULL DEFAULT NULL COMMENT '新闻设置置顶的时机。如果一个栏目多个新闻都设置了置顶，就按该时间决定先后顺序',
  `external_url` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '外网链接，如果新闻是其他网站的链接，则设置外链',
  `inputter_id` int(0) NOT NULL COMMENT '外键，新闻稿的录入人',
  `create_time` datetime(0) NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '新闻的创建时间',
  `complete_input_time` datetime(0) NULL DEFAULT NULL COMMENT '新闻录入完成的时间',
  `editors` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '参与编辑的人员列表，逗号分隔',
  `reviewers` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '参与审核的人员列表，逗号分隔',
  `submitter_id` int(0) NULL DEFAULT NULL COMMENT '外键，提交审核的人，即送审人id',
  `submit_time` datetime(0) NULL DEFAULT NULL COMMENT '送审时间',
  `latest_editor_id` int(0) NULL DEFAULT NULL COMMENT '外键，最近修改新闻的用户id，为了让编辑能够知道最近谁修改了新闻',
  `latest_edit_time` datetime(0) NULL DEFAULT NULL COMMENT '最近修改时间',
  `current_review_epoch` tinyint(0) NOT NULL DEFAULT 0 COMMENT '新闻当前所处的审核轮次',
  `previous_epoch_review_pass_time` datetime(0) NULL DEFAULT NULL COMMENT '当新闻通过一轮审核，就设置该时间，表示这一轮审核通过的时间，下一轮审核的人就可以看到；如果新闻处于待发布状态，则表示新闻通过终审的时间',
  `publisher_id` int(0) NULL DEFAULT NULL COMMENT '外键，新闻发布人的id',
  `real_pub_time` datetime(0) NULL DEFAULT NULL COMMENT '真实的发布时间',
  `show_pub_time` datetime(0) NULL DEFAULT NULL COMMENT '对外显示的发布时间',
  `init_reading_count` int(0) NOT NULL DEFAULT 0 COMMENT '新闻初始阅读量',
  `img_for_show_on_news_list` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '当前新闻显示在新闻列表上的图片',
  `real_reading_count` int(0) NOT NULL DEFAULT 0 COMMENT '新闻实际阅读量',
  `extra` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '存储额外的信息（json格式）。比如，审核失败时，保存审核意见；新闻作为轮播图发布时，保存封面图片地址；新闻被打回修改时，保存修改意见',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 83 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of news
-- ----------------------------
INSERT INTO `news` VALUES (3, '邱水平率团赴上海、浙江、湖南、江西 推进北大与地方合作', '3月29日至4月2日，北京大学党委书记邱水平率团访问上海市、浙江省、湖南省、江西省，拜会主要领导、推进校地合作项目、考察地方研究院建设，并出席选调生座谈会。北京大学党委常委、常务副校长兼教务长、中国科学院院士龚旗煌，党委常委、副校长王博，党委常委、副校长、中科院院士张平文，党委常委、副校长、中科院院士黄如，校长助理、秘书长孙庆伟等出席有关活动。', '<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;3月29日至4月2日，北京大学党委书记邱水平率团访问上海市、浙江省、湖南省、江西省，拜会主要领导、推进校地合作项目、考察地方研究院建设，并出席选调生座谈会。北京大学党委常委、常务副校长兼教务长、中国科学院院士龚旗煌，党委常委、副校长王博，党委常委、副校长、中科院院士张平文，党委常委、副校长、中科院院士黄如，校长助理、秘书长孙庆伟等出席有关活动。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;3月29日，北京大学与中国（上海）自由贸易试验区临港新片区管理委员会就&ldquo;北京大学上海临港国际科技创新中心&rdquo;项目进行合作签约。龚旗煌与临港新片区党工委委员、专职副主任吴晓华代表双方签署合作项目协议。</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.pku.edu.cn/images/2021-04/1f637c857f0c4132ae86c2e5cad6eec5.jpeg\" width=\"396\" height=\"241\" /></p>\n<p style=\"text-align: center;\">签约现场</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;签约后，邱水平一行拜会上海市委书记李强。李强表示，上海和北京大学都传承着红色基因，肩负着光荣使命，承担着党中央赋予的一系列重大国家战略任务。他希望以此次签约为契机，进一步推动市校合作走深走实，加强协同、优势互补、各扬其长，聚焦国际前沿领域和国家战略需求，在强化创新策源功能、培养集聚高端人才、创新科技体制机制等方面深化务实合作。李强欢迎北大深度参与上海发展，上海将营造良好环境、提供优质服务，推动双方合作早日结出丰硕成果。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;邱水平表示，北京大学与上海有着良好合作基础，进入新发展阶段，双方交流对接更加紧密、合作空间更为广阔。北大将充分依托上海优势、发挥自身作用，加快推动临港国际科技创新中心项目建设，拓展深化基础研究、科技创新、产业发展、人文社科等各领域合作，更好融入和参与上海&ldquo;十四五&rdquo;乃至更长时期的发展，在共同服务国家战略中实现更大发展。</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"d13a79baff2f43a9809671884b7fb3f5.jpg\" src=\"https://news.pku.edu.cn/images/2021-04/d13a79baff2f43a9809671884b7fb3f5.jpg\" alt=\"d13a79baff2f43a9809671884b7fb3f5.jpg\" width=\"550\" height=\"365\" border=\"0\" vspace=\"0\" /></p>\n<p style=\"text-align: center;\">李强与邱水平会见</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;在沪期间，邱水平一行还前往中国共产党发起组成立地（《新青年》编辑部）旧址、中国（上海）自由贸易试验区临港新片区考察。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;3月30日，邱水平一行拜会浙江省委书记袁家军。袁家军表示，近些年来，浙江与北京大学携手共进，构建紧密交流协调机制，开展多领域全方面合作，共同建设创新平台，取得了有效成果。他希望双方充分发挥各自优势，在更多领域、更深层次开展务实合作，特别是共同推进浙江省三大科创高地、共同富裕示范区建设。袁家军欢迎更多优秀北大师生到浙江创业创新，浙江一定提供最佳的服务、最优的环境，为他们干事创业搭建宽阔的舞台。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;邱水平表示，北京大学与浙江有着独特渊源，近年来一直保持着密切的交流与合作，省校合作取得积极成效。面向&ldquo;十四五&rdquo;发展，北大将认真落实好双方战略合作协议，进一步加强与浙江全方位合作，全力支持和助力浙江高质量发展，努力推动省校合作结出更加丰硕的成果。</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"eda3084f0ea04e9299a93d94f46b4e1f.jpg\" src=\"https://news.pku.edu.cn/images/2021-04/eda3084f0ea04e9299a93d94f46b4e1f.jpg\" alt=\"eda3084f0ea04e9299a93d94f46b4e1f.jpg\" width=\"550\" height=\"377\" border=\"0\" vspace=\"0\" /></p>\n<p style=\"text-align: center;\">袁家军与邱水平会见</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;3月31日上午，浙江省政府与北京大学续签《浙江省人民政府北京大学战略合作协议》。根据协议，双方将发挥各自优势，整合资源，在战略决策咨询、高能级创新载体建设、科技创新与成果转化、人才干部交流培养等方面开展全方位合作。浙江省委副书记、省长郑栅洁与邱水平见证签约，并共同为数字视频编解码技术国家工程实验室杭州基地、高可信软件技术教育部重点实验室杭州基地、国家集成电路产教融合创新平台杭州基地揭牌。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;在杭期间，邱水平一行还考察调研了北京大学信息技术高等研究院、教育基金会长三角发展办公室，出席了北京大学浙江省选调生座谈会。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;4月1日，邱水平一行拜会湖南省委书记许达哲。许达哲表示，当前，湖南正深入贯彻习近平总书记考察湖南重要讲话精神，深入开展党史学习教育，大力实施&ldquo;三高四新&rdquo;战略，需要有力的科技和人才支撑。他希望北京大学在党史研究、科技创新平台建设、数字经济发展等方面与湖南进一步加强合作，助推湖南走在中部地区崛起前列。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;邱水平表示，湖南文化底蕴深、高等教育实力强、先进制造业发展成效好，与北京大学合作空间广阔。北京大学愿与湖南加强合作，共同进行实践基地建设，助力湖南打造人才高地。</p>\n<p style=\"text-align: center;\"><img title=\"8feaa4e619a643b182d688f265bd0d79.jpg\" src=\"https://news.pku.edu.cn/images/2021-04/8feaa4e619a643b182d688f265bd0d79.jpg\" alt=\"8feaa4e619a643b182d688f265bd0d79.jpg\" width=\"550\" height=\"342\" border=\"0\" vspace=\"0\" /></p>\n<p style=\"text-align: center;\">许达哲与邱水平会见</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;同日，邱水平一行会见了湖南省委常委、长沙市委书记吴桂英。吴桂英表示，北京大学与长沙历史渊源深厚，在科技教育人才文化等方面合作基础良好、前景广阔。她希望在良好合作基础上，构建更加紧密的市校交流合作机制，开展多领域深层次合作，在战略决策咨询、科技创新、人才培养培训等领域取得新成果、开创新局面。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;邱水平表示，北京大学与长沙保持着密切的交流合作。立足新发展阶段，共同肩负历史使命、面临发展机遇，北京大学将与长沙携手服务国家战略实施，深化市校合作，创新体制机制，推动形成稳定长期的全面战略合作关系、结出更加丰硕的合作成果，形成高校服务地方发展、地方助推高校创新的良好格局。</p>\n<p style=\"text-align: center;\"><img title=\"993e5270c0424bfc801c9308789f9f89.jpg\" src=\"https://news.pku.edu.cn/images/2021-04/993e5270c0424bfc801c9308789f9f89.jpg\" alt=\"993e5270c0424bfc801c9308789f9f89.jpg\" width=\"550\" height=\"328\" border=\"0\" vspace=\"0\" /></p>\n<p style=\"text-align: center;\">吴桂英与邱水平会见</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;在长沙期间，邱水平一行还赴湖南第一师范城南书院校区毛泽东与第一师范纪念馆考察。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;4月2日，邱水平一行赴南昌出席北京大学南昌创新研究院揭牌仪式。邱水平与江西省委常委、省委组织部部长刘强为创新研究院揭牌。张平文在揭牌仪式上致辞。揭牌仪式由南昌市委副书记、市长万广明主持。</p>\n<p style=\"text-align: center;\"><img title=\"a7930f45b0e743e78b96877a5c71b11e.jpg\" src=\"https://news.pku.edu.cn/images/2021-04/a7930f45b0e743e78b96877a5c71b11e.jpg\" alt=\"a7930f45b0e743e78b96877a5c71b11e.jpg\" width=\"550\" height=\"367\" border=\"0\" vspace=\"0\" /></p>\n<p style=\"text-align: center;\">揭牌仪式现场</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;北京大学南昌创新研究院由南昌市和北京大学合作共建，落户于南昌高新区的江西省高层次人才产业园。北京大学南昌创新研究院作为江西省、南昌市与北京大学合作实施载体，定位为高水平新型研发机构。研究院落户后，将依托南昌航空产业优势，聚焦航空技术、新材料和先进制造三个领域，打造集技术研发、成果转化、产业孵化、人才培养于一体的产学研用新型创新平台。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;上海市及临港新片区有关部门、浙江省及杭州市有关部门、湖南省及长沙市有关部门、江西省及南昌市有关部门负责同志，北京大学医学部、信息科学技术学院、材料科学与工程学院、化学与分子工程学院、工学院、未来技术学院、党委办公室校长办公室、科技开发部、教育基金会、国内合作委员会办公室、学生就业指导服务中心有关负责同志参加相关活动。</p>', 9, '北京大学新闻网', '北京大学新闻网', 5, 1, '2021-04-23 19:39:19', 1, '2021-04-23 19:39:19', 0, 0, NULL, NULL, 1, '2021-04-13 18:49:48', '2021-04-23 18:43:56', '谢志朋、', '谢志朋、', 1, '2021-04-23 18:44:13', NULL, NULL, 3, '2021-04-23 18:44:34', 1, '2021-04-23 19:39:19', '2021-04-23 19:38:00', 0, 'https://news.pku.edu.cn/images/2021-04/1f637c857f0c4132ae86c2e5cad6eec5.jpeg', 0, '{\"CAROUSEL_IMAGE_URL\":\"https://news.pku.edu.cn/images/2021-04/1f637c857f0c4132ae86c2e5cad6eec5.jpeg\"}');
INSERT INTO `news` VALUES (4, '故宫院长王旭东来访北大并举办讲座', NULL, '<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;4月9日，故宫博物院院长王旭东、副院长赵国英带领相关部门负责人来访北京大学，并与北京大学校长郝平、副校长王博、校长助理孙庆伟以及相关院系领导、负责人在临湖轩举行座谈。会谈后，王旭东为北大师生带来&ldquo;敦煌与故宫&rdquo;主题讲座。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;郝平对王旭东一行的到来表示欢迎。他指出，北京大学与故宫博物院签署战略合作协议以来，双方积极落实、推进合作意向，目前合作取得了一定的进展，希望未来可以开展更加全面、全方位的合作。北京大学非常希望能在中华文明与历史教育、学术交流与人才培养等方面得到故宫博物院的大力支持。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;王旭东对北大在落实推进双方合作方面给予的支持表示感谢。他指出，故宫博物院真诚地希望能在学术研究、文化遗产保护与传播等领域与北京大学开展合作，得到北京大学的支持与帮助，吸引年轻一代学子投身文博事业。近年来，不少文博系统的专家学者走进高校，实现了高校与博物馆之间的人才流通。故宫博物院希望能与北京大学强强联合，相互支持，共同培养学术人才，努力实现时代赋予的使命。</p>\n<p style=\"text-align: center;\"><img title=\"\" src=\"http://news.pku.edu.cn/images/2021-04/fe9f090f5685446bb98d6f3bc0dbb07d.jpeg\" width=\"417\" height=\"280\" /></p>\n<p style=\"text-align: center;\">合影留念</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;座谈结束后，王旭东在王博的陪同下前往二体地下报告厅，为北大师生带来了一场题为&ldquo;敦煌与故宫&mdash;&mdash;中华文化包容互鉴的结晶&rdquo;的学术讲座。该场讲座是北京大学人文社会科学研究院（简称文研院）与故宫博物院联合举办的&ldquo;故宫与故宫学&rdquo;系列讲座的第五讲，也是新学期以来的首讲。300多位北大师生在现场聆听。讲座由文研院院长、北京大学历史学系博雅讲席教授邓小南主持。</p>\n<p style=\"text-align: center;\"><img title=\"\" src=\"http://news.pku.edu.cn/images/2021-04/214180c5c75e4860936b5e924e644cd3.jpeg\" width=\"416\" height=\"278\" /></p>\n<p style=\"text-align: center;\">讲座现场</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;讲座中，王旭东从敦煌与故宫文化的艺术价值、形成来源、发展历程、传承历史以及保护工作等层面深刻阐释了敦煌与故宫的联系和差异，并就敦煌与故宫在文化上共同体现的不同民族间文化交流融合的重要意义与听众进行交流。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;讲座从敦煌与故宫两大文化遗产的形成背景及历史沿革展开。王旭东认为，敦煌保留了&ldquo;灵动&rdquo;&ldquo;变化&rdquo;的艺术形态，精美的石窟彩塑、丰富的壁画内容描摹出不同时期人们生活的方方面面，深刻反映了特定时期的审美特征。彩塑和壁画共同展现了敦煌文化的艺术价值，持续扩大了丝绸之路延展过程中文化交流与传播所产生的巨大影响力。故宫文化则处处体现&ldquo;厚重感&rdquo;，其折射的不仅仅是五千年以来中华民族优秀传统文化的积淀成果，更充分流露出中西文化交流碰撞后的多元色彩。</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-04/b6d15782df4941d88ec5133bdc2df679.jpeg\" width=\"416\" height=\"278\" /></p>\n<p style=\"text-align: center;\">王旭东做讲座</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在比较敦煌和故宫在文化意义上的相通与相异之处时，王旭东指出，敦煌莫高窟和故宫两大文化殿堂是民间信仰和国家支持两种力量共同推动、中外文化交流与中国内部不同民族交融所产生的，可以从中清楚地看到国家力量与民间力量的交互作用。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;王旭东以丰富的图片和史料信息，讲述了敦煌与故宫文物保护工作的基本情况与漫漫历程。从张大千、于右任等有识之士为壁画保护积极奔走，到常书鸿等前辈放弃优渥生活扎根敦煌莫高窟，再到新中国成立后，在国家和社会各界的支持下，洞窟清沙、加固工程持续开展，一代又一代文物工作者前赴后继，为敦煌保护殚精竭虑，倾尽一生心血。随着保护思想、修复手段日趋完善，敦煌也逐渐呈现新的面貌。故宫博物院自开院以来，无论是持续的文物整理与修缮、还是抗日战争文物南迁的艰难历程，都体现了以国家力量为主导的对文物保护的高度重视与大力投入。新中国成立后，故宫的修缮及文物清点工作一直有序地展开。故宫与时代同步，在文物修缮、文物安全、文物展示等方面不断推陈出新，近年来更是利用科技手段、管理手段等打造&ldquo;平安故宫&rdquo;&ldquo;学术故宫&rdquo;&ldquo;数字故宫&rdquo;&ldquo;活力故宫&rdquo;，让文物真正&ldquo;活起来&rdquo;，践行&ldquo;完整地保护并负责任地传承弘扬故宫承载的中华优秀传统文化&rdquo;的初心。王旭东希望北京大学与故宫进一步完善互访机制，在一次次故宫参访中传播故宫文化，真正肩负起保护故宫文化的社会责任。</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-04/a290bac1b1284eb7969c7eaf063f2945.jpeg\" width=\"416\" height=\"269\" /></p>\n<p style=\"text-align: center;\">师生认真聆听</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;讲座最后，王旭东强调，新中国成立后敦煌与故宫的保护发展启示我们应坚定文化自信，以更加宽阔的胸怀、更加开放包容的心态吸收人类文明成果，建设文化强国，践行构建人类命运共同体的伟大理念。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;互动交流环节，王旭东就听众提出的&ldquo;故宫文化爱好者如何对故宫文化传播作出贡献&rdquo;&ldquo;流失文物如何回归&rdquo;等问题与大家进行交流讨论。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;邓小南在主持讲座时表示，王旭东的讲座内容详实、视野宏阔、视角独特，生动的讲述加深了现场师生的文化体验。文研院将与故宫一道，为双方的合作探寻新的途径与发展模式，共同为促进中华民族优秀文化创新发展作出贡献。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;在北京大学开启&ldquo;故宫与故宫学&rdquo;系列讲座的同时，故宫博物院也开展了&ldquo;北京大学系列学术讲座&rdquo;，双方互邀知名专家学者就故宫学、清代宫廷史、历史学、古书画等重点领域开设讲坛。2020年春季学期，故宫与北大合作开设文研课程&ldquo;中国古代书画鉴定与鉴藏&rdquo;&ldquo;明清宫廷建筑与宫殿陈设&rdquo;。故宫专家将故宫特色学术研究引入课堂，实现故宫学术进北大。在北大与故宫的共同努力下，双方的学术交流合作不断深化，并逐渐迈上新的层次。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;接下来，&ldquo;故宫与故宫学&rdquo;系列讲座还将继续。北京大学和故宫博物院的相关专家、学者将围绕博物馆学、古文献学、古书画、宋代文学、清宫典仪等领域展开精彩讲座。具体讲座信息将在文研院公众号、&ldquo;故宫研究院&rdquo;公众号上发布。</p>', NULL, '北京大学新闻网', '北京大学新闻网', 0, 0, NULL, 0, NULL, 0, 0, NULL, NULL, 1, '2021-04-13 18:53:30', NULL, NULL, NULL, NULL, NULL, NULL, '2021-04-13 18:53:31', 0, NULL, NULL, NULL, NULL, 0, NULL, 0, NULL);
INSERT INTO `news` VALUES (5, '自强成就卓越 创新塑造未来——清华大学110周年校庆献辞', '清和四月，树木华滋。在清华大学即将迎来110周年华诞的喜庆时刻，我们代表学校向全校师生员工和海内外校友致以节日的问候！向长期关心支持学校事业发展的各级领导部门、各单位和各界朋友表示衷心的感谢！', '<p class=\"vsbcontent_start\"><strong>●&nbsp;</strong>校长<strong>&nbsp;邱勇&nbsp;</strong>校党委书记<strong>&nbsp;陈旭</strong></p>\n<p style=\"text-align: center;\"><img class=\"img_vsb_content\" title=\"\" src=\"https://news.tsinghua.edu.cn/__local/D/36/45/E652F28EF6733C73B8B4DEE5B56_6B6571FC_1F4A1.jpg\" width=\"554\" /></p>\n<p class=\"vsbcontent_start\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;清和四月，树木华滋。在清华大学即将迎来110周年华诞的喜庆时刻，我们代表学校向全校师生员工和海内外校友致以节日的问候！向长期关心支持学校事业发展的各级领导部门、各单位和各界朋友表示衷心的感谢！</p>\n<p><strong>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;西山苍苍，东海茫茫</strong>。110年前，清华诞生于中国内忧外患、山河破碎的深重苦难中。自建校起，自强不息的民族精神、&ldquo;耻不如人&rdquo;的不屈品格就深深融入清华人的血脉里。清华人秉持科学救国理想，将清华从一所留美预备学校建设成为一所人民的大学、一所实现了教育与学术自立自强的现代大学。从&ldquo;一二&middot;九&rdquo;运动的一声呐喊到西南联大的&ldquo;八音合奏&rdquo;，从新中国成立后建设多科性工业大学的积极探索到乘着改革开放春风吹响创建世界一流大学的号角，清华大学在长期办学实践中形成了深厚的文化积淀和光荣的革命传统。<strong>&ldquo;自强不息、厚德载物&rdquo;的校训、&ldquo;行胜于言&rdquo;的校风、&ldquo;严谨、勤奋、求实、创新&rdquo;的学风、&ldquo;爱国奉献、追求卓越&rdquo;的精神，塑造了清华人的意志品质，涵养了清华人的气度风范。一代代清华人接续奋斗，为祖国、为人民、为民族建立了突出功绩，走出了一条扎根中国大地建设世界一流大学之路</strong>。</p>\n<p><strong>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;春风化雨，其乐未央。清华始终将育人置于最重要、最根本的位置</strong>。1911年，《清华学堂章程》中规定&ldquo;以培植全材，增进国力为宗旨&rdquo;。梅贻琦校长提出&ldquo;从游论&rdquo;，强调&ldquo;发展全人格&rdquo;&ldquo;通识为本&rdquo;。新中国成立后，蒋南翔校长倡导&ldquo;又红又专、全面发展&rdquo;&ldquo;因材施教&rdquo;的教育理念，大力推进政治辅导员、科学登山队、文艺与体育&ldquo;三支学生代表队&rdquo;的建设。改革开放后，学校坚持高素质、高层次、多样化、创造性的人才培养目标，致力于培养学生具备健全人格、宽厚基础、创新思维、全球视野和社会责任感，引导学生成长为肩负使命、追求卓越的时代新人。<strong>来自五湖四海的莘莘学子在宁静优美、生机勃勃、昂扬向上的清华园里度过了难忘的青春时光并奔向&ldquo;祖国最需要的地方&rdquo;，从中涌现出了一大批治学、兴业、治国的优秀人才</strong>。</p>\n<p><strong>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;致知穷理，学问笃实</strong>。1925年，清华成立大学部并设国学研究院，欲&ldquo;建设最高等学术&rdquo;。无论是开创融会中西、古今、文理的学术风格，还是确立&ldquo;顶天、立地、树人&rdquo;的科研宗旨；无论是参与&ldquo;两弹一星&rdquo;、密云水库等重大工程，承担国徽、人民英雄纪念碑等重要设计，还是创造高温气冷堆、大型集装箱检测系统等重要成果；无论是开辟中国现代诸多科学技术与文化艺术学术领域，兴办一批国家现代化建设亟需的新兴专业，还是明确综合性、研究型、开放式的总体办学思路，<strong>清华大学面向世界学术前沿和国家重大战略需求，填补了一项又一项空白，取得了一个又一个突破，发展成为我国科学技术和思想文化创新的重要基地</strong>。</p>\n<p><strong>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;人文日新，行健不息</strong>。进入新时代，清华大学开启了一场使命驱动的改革。<strong>学校坚持和加强党的全面领导，将世界一流、中国特色、清华风格统一在办学实践中</strong>。以教师队伍人事制度改革为突破口，深入实施人才强校核心战略。深化教育教学改革，确立价值塑造、能力培养、知识传授&ldquo;三位一体&rdquo;教育理念，完善全员、全过程、全方位的育人体系。健全学科分类建设机制，不断增强学术创新能力，加快哲学社会科学繁荣发展，在低维量子物理、结构生物学、密码学、类脑计算芯片、量子计算等领域取得一系列国际领先成果。改革社会服务体制机制，扎实做好对口支援和定点帮扶工作。制定实施全球战略，成立全球创新学院、中意设计创新基地、东南亚中心、拉美中心，创办苏世民书院、深圳国际研究生院，发起亚洲大学联盟、世界大学气候变化联盟、世界慕课联盟并担任主席单位，全球声誉显著提升。<strong>学校深入推进&ldquo;双一流&rdquo;建设，&ldquo;三个九年，分三步走&rdquo;总体战略目标如期实现，创建世界一流大学取得历史性跨越</strong>。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;在让我们刻骨铭心的2020年，清华大学始终把师生的生命安全和身体健康放在首位，坚持疫情防控不松懈、教学科研不停步、改革发展不放松，引领在线教育变革，全力推进抗疫科研攻关，加强抗疫国际交流合作，<strong>展现了面对危机和挑战时一所大学应有的责任担当，铸就了新时代教书育人的新气象</strong>。</p>\n<p><strong>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;大同爰跻，祖国以光。习近平总书记指出：&ldquo;办好高等教育，事关国家发展、事关民族未来</strong>。&rdquo;在奋力迈向世界一流大学前列的新阶段，清华大学将以习近平新时代中国特色社会主义思想为指导，牢牢把握学校改革发展的最好历史时期，坚持正确办学方向，落实立德树人根本任务，完善教育评价制度，打造一流创新体系，构建良好学术生态，全面深化改革，不断提升治理能力，努力实现新的引领，力争为国家发展和人类进步作出新的更大贡献。</p>\n<p class=\"vsbcontent_end\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&ldquo;一国之大学，当有其对於一国之任务；一代之大学，当有其处於一代之特点。&rdquo;<strong>自强成就卓越，创新塑造未来。在全面建设社会主义现代化国家的新征程上，自强的清华人永远以创新为矢志不渝的追求，永远保持奋进的姿态，努力开拓学校发展新格局，更自信、更从容地续写新的清韵华章，以优异成绩献礼建党100周年</strong>！</p>\n<p style=\"text-align: right;\">题图：霍元东</p>\n<p style=\"text-align: right;\">编辑：赵姝婧 陈晓艳</p>\n<p style=\"text-align: right;\">审核：吕婷</p>', NULL, '清华大学新闻网', '清华大学新闻网', 7, 0, NULL, 0, NULL, 0, 0, NULL, NULL, 1, '2021-04-13 18:59:26', '2021-04-13 18:59:26', '谢志朋、', NULL, NULL, NULL, 1, '2021-04-20 20:43:09', 0, NULL, NULL, NULL, NULL, 0, NULL, 0, '{\"RE_MODIFICATION\":{\"operateTime\":\"2021-04-23 19:26:29\",\"operator\":{\"id\":1},\"suggestion\":\"系统温馨提示：系统的审核流程发生变化，所有审核中的新闻全部回到中转站！\"}}');
INSERT INTO `news` VALUES (6, '滴滴出行支持武汉大学高水平人才培养启动仪式举行', NULL, '<p class=\"vsbcontent_start\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;新闻网讯（通讯员刘丹、舒悦）4月9日下午，滴滴出行支持武汉大学高水平人才培养启动仪式在我校举行。以此为契机，武汉大学和滴滴将在高水平人才培养、人才招聘、科研合作、课程建设等方面开启合作。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;副校长吴平，滴滴联合创始人兼CTO、滴滴自动驾驶公司CEO张博校友，滴滴出行方代表，我校相关职能部门及院系负责人，计算机学院师生代表出席了活动。</p>\n<p>武汉大学教育发展基金会副理事长兼秘书长邓小梅和滴滴出行科技生态与发展部总监吴国斌代表合作双方签署了相关协议。吴平向张博特别赠送了其在校期间的学生档案作为&ldquo;珞珈时光&rdquo;礼品。</p>\n<p class=\"vsbcontent_img\" style=\"text-align: center;\"><img class=\"img_vsb_content\" title=\"\" src=\"https://news.whu.edu.cn/__local/F/0B/A8/60E11662DAEDF120EB54C6C73A0_C6403637_1A65A.png\" width=\"600\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;张博对母校长期以来对滴滴的支持表示感谢，希望能与母校在共享出行核心关键技术、自动驾驶、汽车创新研发、智能出行人工智能平台等方面共同探索前沿，打造人才培养新机制，在人才、技术和场景等多方面优势互补，为社会发展做出贡献。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;吴平指出，滴滴出行是国家科技创新的代表性企业之一，在智慧交通、机器学习、数据挖掘和大数据等领域，取得了多项业界领先的技术成果，成绩斐然。双方开启人才联合培养，契合学校人才培养的需要。武汉大学将以此为契机，定向培养一批应用型、创新型人才，使学校的人才培养与社会接轨、与行业需求接轨，符合发展趋势，满足社会需求。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;张博为在场400余位武大学子带来题为&ldquo;用科技D造未来&rdquo;的主题演讲，并现场发布滴滴未来精英人才品牌，特别介绍了滴滴近期开启的未来精英实习生计划和博士后工作站，邀请包括武汉大学在内的全球高校科技人才成为滴滴未来精英，一同在多元化的业务场景里，攻克核心技术难题和探索技术前沿，让世界变得更加美好。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;返校期间，张博还拜访了校长窦贤康院士、副校长李建成院士，在计算机学院召开了&ldquo;滴滴未来精英&middot;校园行&rdquo;精英人才圆桌论坛。张博是我校国际软件学院2005届软件工程本科毕业生。</p>\n<p class=\"vsbcontent_end\" style=\"text-align: right;\">（编辑：肖珊）</p>', NULL, '武大新闻网', '武大新闻网', 3, 0, NULL, 0, NULL, 0, 0, NULL, NULL, 1, '2021-04-13 20:32:44', '2021-04-13 20:39:55', '谢志朋、', NULL, 1, '2021-04-16 21:09:02', 1, '2021-04-16 21:09:02', 1, NULL, NULL, NULL, NULL, 0, NULL, 0, '{\"REVIEW_FAIL\":{\"epoch\":1,\"reviewTime\":\"2021-04-16 21:10\",\"reviewer\":{\"id\":1},\"suggestion\":\"测试一审审核失败\"}}');
INSERT INTO `news` VALUES (25, '湖南省政协副主席赖明勇来校调研人才工作', '4月8日下午，湖南省政协副主席赖明勇率队来校调研人才工作。校党委书记易红、副书记李亮参加调研。 当日下午，赖明勇一行参观了高性能复杂制造国家重点...', '<div class=\"subCont\">\n<div class=\"v_news_content\">\n<p class=\"vsbcontent_start\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;本网讯 &nbsp;4月8日下午，湖南省政协副主席赖明勇率队来校调研人才工作。校党委书记易红、副书记李亮参加调研。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;当日下午，赖明勇一行参观了高性能复杂制造国家重点实验室、国家重金属污染防治工程技术研究中心。在科教楼三会议室，与学校相关单位代表座谈，重点围绕&ldquo;进一步完善引进和留住人才的激励政策，为实施&lsquo;三高四新&rsquo;战略提供人才支撑&rdquo;进行讨论。</p>\n<p class=\"vsbcontent_img\"><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"\" src=\"http://news.csu.edu.cn/__local/9/D7/87/3AEDBF5F01EC5FA7C9D03D88E27_BB6AB14D_251C14.jpeg\" alt=\"\" width=\"600\" height=\"400\" border=\"0\" hspace=\"0\" vspace=\"0\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;近年来，中南大学在人才队伍建设方面注重谋篇布局，专门成立人才工作领导小组，加强顶层设计，健全人才引育体系；注重全球延揽，拓宽引才视野，创新工作思路，实施柔性引智计划；注重靶向培育，坚持引育并举，设立战略先导专项，关注青年人才成长；注重精细服务，建立党委联系服务专家制度，推行一站式服务，深化&ldquo;放管服&rdquo;改革，建立人才择优选聘机制，使得人才优先发展、协同发展、提速发展、潜心发展。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;赖明勇指出，创新是第一动力，人才是第一资源。充分利用好高校人才资源是做好湖南省人才建设工作的重要组成。中南大学是湖南的科技创新高地和人才聚集高地，在引才、育才以及稳定人才方面的政策、举措、制度及建设性意见，为全省人才队伍建设工作提供了可靠数据和参考意见。希望学校进一步探索和提供好的经验做法，加大人才队伍建设力度，为实施&ldquo;三高四新&rdquo;战略提供更大的人才和智力支撑。</p>\n<p class=\"vsbcontent_end\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;湖南省政协相关单位负责人，学校党委组织部、统战部、人事处、科研部、研究生院、物电院、材料院及湘雅医院等单位代表参加座谈。</p>\n</div>\n</div>\n<p>&nbsp;</p>', 1, '中南大学新闻网', '中南大学新闻网', 5, 0, NULL, 0, NULL, 0, 1, '2021-04-23 21:10:50', NULL, 1, '2021-04-13 21:08:47', '2021-04-13 21:08:47', '谢志朋、王五、钱六六、赵七、', '谢志朋、', 1, '2021-04-20 21:45:55', 1, '2021-04-20 21:45:55', 3, '2021-04-20 21:47:46', 1, '2021-04-23 21:10:50', '2021-04-20 21:49:00', 312, 'http://news.csu.edu.cn/__local/9/D7/87/3AEDBF5F01EC5FA7C9D03D88E27_BB6AB14D_251C14.jpeg', 0, NULL);
INSERT INTO `news` VALUES (38, '【聚焦就业】落实改革见行动，就业工作呈新貌', NULL, '<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;2021年，学校党委启动了就业工作综合改革，下达了打好就业工作翻身仗的动员令。全校各单位将就业工作与开展党史教育活动相结合，积极行动，协同奋进，密切配合，就业工作稳步推进，呈现出崭新面貌。</p>\n<p style=\"text-align: center;\"><strong>下沉基层，就业工作更深入</strong></p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/ba9f0e565c.png\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>　　4月9日，副校长欧阳康带队先后到法学院、人文学院、会计学院调研毕业生就业工作。每到一处，欧阳康都详细询问了毕业生就业动态数据、就业工作举措、就业工作中存在的难点和问题，听取了各个单位的建议，提出了工作要求。欧阳康表示，今后还将继续深入各单位了解就业工作情况，与各单位群策群力，共同讨论就业难题的破解之道。</p>\n<p>　　此前，招生就业处处长柳晨带队先后到会计学院、金融学院、财税与公共管理学院、马克思主义学院等单位调研就业工作，与各单位共同商议如何推进就业工作改革。</p>\n<p style=\"text-align: center;\"><strong>明确责任，就业工作更务实</strong></p>\n<p>　　各单位积极响应校党委号召，进一步统一思想认识，提高政治站位，牢固树立就业工作全员化理念，明确职责分工，将就业工作责任落到实处。各单位党政一把手齐抓共管，对就业工作同部署、同调度、同推进。如会计学院进一步压实研究生就业责任，将研究生导师和研究生班主任与研究生一一对应，落实细化就业工作职责。</p>\n<p style=\"text-align: center;\"><strong>建立台账，就业工作更细致</strong></p>\n<p>　　学校《就业工作综合改革方案》印发之后，招生就业处立即将本单位承担的改革任务进行分工，并将责任落实到人，建立就业工作台账，实施任务进展每日一报制度。各学院都根据本院毕业生实际情况，建立就业工作台账，将工作任务明确到个人。如马克思主义学院实施挂图作战，就业一人销账一个，实行就业工作的&ldquo;一人一号&rdquo;&ldquo;一人一策&rdquo;举措。</p>\n<p style=\"text-align: center;\"><strong>走进学生，就业工作更精准</strong></p>\n<p>　　财税与公共管理学院、会计学院、法学院、人文学院等深入了解考研学生的成绩及上线情况，针对报考不同院校的学生分别提供专人指导服务，针对参加复试、申请调剂和未上线的毕业生精准施策，做好指导、服务和引导工作。招生就业处深入了解报考公职岗位的培训需求，通过与第三方机构合作，为有需求的毕业生提供考前冲刺培训。各学院针对身体条件好、国防意识强的毕业生，积极做好征兵宣传工作，讲清最新政策，鼓励毕业生参军入伍。</p>\n<p style=\"text-align: center;\"><strong>开</strong><strong>拓市场，就业工作更主动</strong></p>\n<p>　　招生就业处广开门路，积极开拓省内省外两个市场，联系用人单位来校招聘，截止3月底，已为2021届毕业生引进就业岗位7万余个。各培养单位主动作为，广泛联系用人单位来校来院招聘。旅游与城市管理学院院领导带队赴上海等地开拓就业市场，软件与物联网工程学院积极邀请用人单位来校举行院级双选会，工商管理学院、会计学院、统计学院等正筹划举办中小型双选会，进一步丰富岗位供给。（文/招生就业处&nbsp;&nbsp;&nbsp; 编辑/姜莹&nbsp; 周雨菲）</p>', NULL, '江西财经大学新闻网', '江西财经大学新闻网', 0, 0, NULL, 0, NULL, 0, 0, NULL, NULL, 1, '2021-04-13 23:49:11', NULL, NULL, NULL, NULL, NULL, NULL, '2021-04-13 23:52:10', 0, NULL, NULL, NULL, NULL, 0, NULL, 0, NULL);
INSERT INTO `news` VALUES (40, '弘扬英雄精神 黄群塑像在校揭幕', NULL, '<p class=\"vsbcontent_start\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;新闻网讯（记者 张思晗）4月9日，黄群塑像揭幕仪式在船海学院东二楼庭院举行。校长李元元、黄群校友夫人亢群共同为黄群塑像揭幕。&ldquo;黄群班&rdquo;学生代表为塑像敬献鲜花。</p>\n<p class=\"vsbcontent_start\">&nbsp;</p>\n<p class=\"vsbcontent_img\" style=\"text-align: center;\"><img class=\"img_vsb_content\" src=\"http://news.hust.edu.cn/__local/8/38/39/30980BF057D3C55397BC7783263_7D02D63E_17E15.jpg\" width=\"500\" data-bd-imgshare-binded=\"1\" /></p>\n<p class=\"vsbcontent_img\">&nbsp;</p>\n<p class=\"vsbcontent_img\" style=\"text-align: center;\"><img class=\"img_vsb_content\" src=\"http://news.hust.edu.cn/__local/8/A4/EE/8E315CD308496547DD085D72045_194C37BC_177FB.jpg\" width=\"500\" data-bd-imgshare-binded=\"1\" /></p>\n<p class=\"vsbcontent_img\">&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;2018年8月20日，黄群和队友因在台风中抢救国家重点试验平台壮烈牺牲。习近平总书记作出重要批示，黄群等3位同志用实际行动诠释了共产党员对党忠诚、恪尽职守、不怕牺牲的优秀品格，用宝贵生命践行了共产党员&ldquo;随时准备为党和人民牺牲一切&rdquo;的初心和誓言，他们是共产党员的优秀代表、时代楷模。此次落成的黄群塑像材质由铜、锈蚀钢、不锈钢组成，人像有70公分，基座140公分；内部为凹凸不平的锈蚀钢构成，高低搭配组合，肌理参照码头防护墙。外部为拉丝不锈钢，以浪花为参照蓝本。</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;李元元代表学校向捐资筹建黄群塑像的校友师生表示感谢。他指出，习近平总书记充分肯定了黄群同志的先进事迹，作为黄群同志的母校，我们有责任继承好黄群同志的精神，薪火相传、代代承续。李元元强调，当前学校正在掀起党史学习教育热潮，全校师生要进一步深入学习、宣传黄群同志的先进事迹，将其转化为思想教育优质教材；要进一步讲好新时代华中大故事，用身边人说身边事，用身边事带动身边人；要进一步凝练&ldquo;黄群班&rdquo;建设经验，将&ldquo;黄群精神&rdquo;融入立德树人工作；要进一步提振干事创业精气神，脚踏实地、埋头苦干，确保&ldquo;十四五&rdquo;开好局、起好步，为中国共产党成立100周年献礼。</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;亢群表示，黄群是亲人、是战友、是挚爱。黄群从船海学院启航，在中国共产党的领导下，在对理想信念的不断追求下，逐渐成长为一名目标明确、技术过硬、管理一流的优秀共产党员，一名为事业献出宝贵生命的时代楷模。她希望黄群同志将入党誓词贯穿于生命、将强军梦想牢记于心中、将宝贵生命献给钟爱事业的英雄壮举，滋养同学们的心灵，激励同学们树立正确的人生观和价值观。</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;船海学院院长解德介绍了黄群同志的生平事迹和&ldquo;黄群班&rdquo;建设情况。他指出，黄群同志是我校众多杰出校友中的光荣典范，其身上体现出的&ldquo;老黄牛&rdquo;精神，充分展现了华中大优秀学子的精神风貌。大家要以黄群同志为榜样，不忘初心，牢记使命，坚持立德树人根本任务，为建设海洋强国培养出更多的卓越人才。</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;中国船舶集团第760研究所纪委书记贾占滨介绍了黄群同志所负责试验平台的建设情况，希望同学们以英烈为榜样，在校园里孜孜以求、发奋图强，为中国梦强国梦的实现贡献力量。</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;首届&ldquo;黄群班&rdquo;班党支部书记刘娇汇报了班级建设情况，表示将坚持学习、传承、践行&ldquo;黄群精神&rdquo;，勤学笃行、矢志报国，做德智体美劳全面发展的合格建设者和可靠接班人。</p>\n<p>&nbsp;</p>\n<p class=\"vsbcontent_img\"><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.hust.edu.cn/__local/7/AC/2E/70A50607D0F7947EFA87DE78645_B689FA95_18882.jpg\" alt=\"18882\" width=\"500\" data-bd-imgshare-binded=\"1\" /></p>\n<p class=\"vsbcontent_img\">&nbsp;</p>\n<p style=\"text-align: center;\">记者 刘涵木 摄</p>\n<p>&nbsp;</p>\n<p class=\"vsbcontent_end\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;学校相关部门负责人，船海学院党政班子成员，中国船舶集团第760研究所、第719研究所代表，以及师生代表等参加活动。</p>', NULL, '华中科技大学新闻网', '华中科技大学新闻网', 0, 0, NULL, 0, NULL, 0, 0, NULL, NULL, 1, '2021-04-14 10:43:58', NULL, NULL, NULL, NULL, NULL, NULL, '2021-04-14 10:46:12', 0, NULL, NULL, NULL, NULL, 0, NULL, 0, NULL);
INSERT INTO `news` VALUES (42, '公卫学院携手硚口科协助力社区健康科普', '4月9日上午，结合湖北省营养协会举办的“全民营养周”契机，公卫学院携手武汉市硚口区科协，举行了今年第二场“健康新时代、青春公卫行”活动，助力硚口集贤里社区健康科普。', '<p class=\"vsbcontent_start\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;新闻网讯（通讯员 邸红昆）4月9日上午，结合湖北省营养协会举办的&ldquo;全民营养周&rdquo;契机，公卫学院携手武汉市硚口区科协，举行了今年第二场&ldquo;健康新时代、青春公卫行&rdquo;活动，助力硚口集贤里社区健康科普。此次活动以&ldquo;老年人营养与健康&rdquo;为主题，学院30余名本硕博师生和党员骨干组成志愿服务团，走进集贤里社区，为社区内的老年居民进行健康知识科普宣教服务。公卫学院党委副书记冯霞、硚口区科协黄来生副主席、郑春阳主任参与活动。</p>\n<p class=\"vsbcontent_start\">&nbsp;</p>\n<p class=\"vsbcontent_img\"><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.hust.edu.cn/__local/7/17/A0/D7D26EEDB4ABA2EE933C9AD397C_3C9AC036_1C81B.jpg\" width=\"500\" data-bd-imgshare-binded=\"1\" /></p>\n<p class=\"vsbcontent_img\">&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;上午9：00，湖北省营养学会常务理事、公卫学院营养与食品卫生学系的郝丽萍教授，声情并茂地为在场的居民进行了一场深入浅出的营养健康知识宣讲。郝丽萍首先提到我国于2019年出台的《健康中国行动（2019&mdash;2030年）》，围绕疾病预防和健康促进两大核心提出15个重大专项行动，促进以&ldquo;治病&rdquo;为中心向以&ldquo;健康&rdquo;为中心转变，努力使群众不生病、少生病。她围绕合理膳食和老年健康促进两大专项行动，分别向居民介绍了人体营养需要、中国居民膳食指南和实践应用、以及老年人生理代谢特点和营养需求。郝丽萍指出，随着年龄的增长，老年人新陈代谢降低，对营养的需求增加了，对能量的需求降低了，每日膳食应遵循《中国老年人膳食指南》，努力做到合理膳食、均衡营养，减少和延缓疾病的发生发展。</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;宣讲内容贴近实际、贴近生活、贴近群众，讲透了观点，讲清了重点，具有很强的吸引力和感染力。宣讲结束后，反响热烈，郝丽萍就武汉传统早餐&ldquo;热干面&rdquo;和植物雌激素等保健品适不适合老年人每日食用等问题与在场的居民进行了深切的交流。郝丽萍提出适合老年人特点的膳食指导，才能更好适应身体功能改变。</p>\n<p>&nbsp;</p>\n<p class=\"vsbcontent_img\"><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.hust.edu.cn/__local/7/0B/61/277554E2D8893F161D5222E603A_5E36DF4D_27C1B.jpg\" width=\"500\" data-bd-imgshare-binded=\"1\" /></p>\n<p class=\"vsbcontent_img\">&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;为做到点对点精准宣传，让健康知识落地生根、入脑入心。志愿服务团的十余名党员志愿者们到社区入户开展宣传工作，分发《预防慢性病（糖尿病、高血压）》和《保健食品小知识》等资料，将慢病防治知识以及健康常识传达至各户居民，并发放口罩和洗手凝胶等公共卫生物品，教育引导居民提升疫情防护意识。</p>\n<p class=\"vsbcontent_img\">&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;随后，我院党员骨干为在场的社区居民进行肩颈操和七步洗手法的现场教学。现场居民和志愿者们一同学习效仿，身心得到了放松，现场气氛达到了高潮。</p>\n<p>&nbsp;</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.hust.edu.cn/__local/D/73/CC/EE789E20F26985FA8AE326E38AB_D9A75C36_1F873.jpg\" width=\"500\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;身穿白大褂的志愿者们为社区居民提供血压测量和健康咨询服务，他们耐心询问了居民们的日常生活行为方式，并对居民们提出的自身健康问题进行了积极的解答，同时纠正了居民们一些错误的健康观和养生观。</p>\n<p>&nbsp;</p>\n<p class=\"vsbcontent_img\"><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.hust.edu.cn/__local/5/CC/3A/24311FB57A6E0951B3468D3B20F_E696B2B5_23AB7.jpg\" width=\"500\" data-bd-imgshare-binded=\"1\" /></p>\n<p class=\"vsbcontent_img\">&nbsp;</p>\n<p style=\"text-align: center;\">通讯员 骆曜宇 摄</p>\n<p>&nbsp;</p>\n<p class=\"vsbcontent_end\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;本次活动得到了硚口区科协和社区居民的认可，居民们表示，这样的讲座很接地气、很受用，让我们老年居民对自己和家人的健康和防疫知识有了科学的认识。参加此次活动的公卫学子们，通过这样的科普健康社会实践活动更加深刻认识到自己应该承担的时代责任。</p>', 15, '华中科技大学新闻网', '华中科技大学新闻网', 5, 0, NULL, 0, NULL, 0, 0, NULL, NULL, 1, '2021-04-14 12:29:58', '2021-04-14 21:27:40', '谢志朋、王五、', NULL, 1, '2021-04-23 19:29:18', 1, '2021-04-20 21:30:32', 1, '2021-04-23 19:29:18', 1, '2021-04-23 19:57:30', '2021-04-23 19:57:00', 600, 'http://news.hust.edu.cn/__local/5/CC/3A/24311FB57A6E0951B3468D3B20F_E696B2B5_23AB7.jpg', 0, NULL);
INSERT INTO `news` VALUES (67, '【迎百年 学党史】校妇委会开展喜迎建党100周年东湖绿道健步行活动', NULL, NULL, NULL, NULL, NULL, 0, 0, NULL, 0, NULL, 0, 0, NULL, 'http://news.hust.edu.cn/info/1002/41536.htm', 1, '2021-04-15 20:06:29', NULL, NULL, NULL, NULL, NULL, NULL, '2021-04-15 20:06:30', 0, NULL, NULL, NULL, NULL, 0, NULL, 0, NULL);
INSERT INTO `news` VALUES (68, '竹外桃花三两枝 春江水暖鸭先知', '3月的燕园春和景明、水波荡漾，扑面而来的温柔煦风，浸润着春日的美好气息。春分前后的细密酥雨，滋润满园芳菲，催绿枝头青芽，更让园子里的小动物们“抖擞”了精神。竹外桃花三两枝，静享闲然悠趣，若不想辜负大美春光，请跟随我们的相机，记录下这满园生趣。', '<p style=\"text-align: left;\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;经历北方漫长的冬季，春姑娘翩然而至。3月的燕园春和景明、水波荡漾，扑面而来的温柔煦风，浸润着春日的美好气息。春分前后的细密酥雨，滋润满园芳菲，催绿枝头青芽，更让园子里的小动物们&ldquo;抖擞&rdquo;了精神。竹外桃花三两枝，静享闲然悠趣，若不想辜负大美春光，请跟随我们的相机，记录下这满园生趣。</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-03/18b06166d596494ca4142f02c8eebab1.jpeg\" width=\"554\" height=\"312\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-03/da0b6fbbfa5b47c79cbdac0fd0b9d86d.jpeg\" width=\"554\" height=\"312\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-03/efc4fd4b2b2f43459a4da810e4c45a09.jpeg\" width=\"552\" height=\"311\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-03/62d68245d41c4dc4b8109b4e5333dcf8.jpeg\" width=\"554\" height=\"312\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-03/c211b3b96fa541908b781370ab605ab1.jpeg\" width=\"554\" height=\"312\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-03/b3c050212bff42048d02d72c77706e80.jpeg\" width=\"554\" height=\"312\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-03/0d2294195ff6401ba632ba4200d90a49.jpeg\" width=\"554\" height=\"312\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"ee5e02717cef47a099530567f9e984c1.jpg\" src=\"http://news.pku.edu.cn/images/2021-03/ee5e02717cef47a099530567f9e984c1.jpg\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"16024f7b96c9405b8df3c640c8338d09.jpg\" src=\"http://news.pku.edu.cn/images/2021-03/16024f7b96c9405b8df3c640c8338d09.jpg\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"bd22368c3cf34e8cbcc4a6a911e512af.jpg\" src=\"http://news.pku.edu.cn/images/2021-03/bd22368c3cf34e8cbcc4a6a911e512af.jpg\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"beaf3a02063347aab78f1fc0f7bb7056.jpg\" src=\"http://news.pku.edu.cn/images/2021-03/beaf3a02063347aab78f1fc0f7bb7056.jpg\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"2b1550b7473e4a93a28898ab0bbd680e.jpg\" src=\"http://news.pku.edu.cn/images/2021-03/2b1550b7473e4a93a28898ab0bbd680e.jpg\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"a78b5c99dea84c29bd10d41e91468f68.jpg\" src=\"http://news.pku.edu.cn/images/2021-03/a78b5c99dea84c29bd10d41e91468f68.jpg\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-03/c43bdf81c11a4dfebaab0c81de207b61.jpeg\" width=\"554\" height=\"312\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"3226bd7c1ece4652953f6acf81d4bdda.jpg\" src=\"http://news.pku.edu.cn/images/2021-03/3226bd7c1ece4652953f6acf81d4bdda.jpg\" alt=\"df3897e52c69f2c4670d4744ac917ef_调整大小.jpg\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\"><img title=\"\" src=\"http://news.pku.edu.cn/images/2021-03/8a83616b71944c67ae89588ee9c87c94.jpg\" alt=\"微信图片_20210323172133.jpg\" /></p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>\n<p style=\"text-align: center;\">&nbsp;</p>', 6, '北京大学新闻网', '北京大学新闻网', 5, 1, '2021-04-23 21:04:46', 0, NULL, 0, 0, NULL, NULL, 1, '2021-04-16 17:58:30', '2021-04-16 17:58:31', '谢志朋、', '谢志朋、', 1, '2021-04-20 21:44:50', 1, '2021-04-20 21:44:50', 3, '2021-04-20 21:47:42', 1, '2021-04-23 21:04:46', '2021-04-20 21:48:00', 0, 'http://news.pku.edu.cn/images/2021-03/efc4fd4b2b2f43459a4da810e4c45a09.jpeg', 0, '{\"CAROUSEL_IMAGE_URL\":\"http://news.pku.edu.cn/images/2021-03/62d68245d41c4dc4b8109b4e5333dcf8.jpeg\"}');
INSERT INTO `news` VALUES (69, '“庆百年荣光 谱青春芳华”——光华管理学院首届“光华杯”迎春跑活动举行', '4月10日，由光华管理学院工会和团委共同举办的首届“光华杯”迎春跑活动举行，活动以“庆百年荣光，谱青春芳华”为主题，将党史学习教育融入到春季户外跑步当中，号召全院师生以协力奔跑的形式献礼建党百年。', '<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;4月10日，由光华管理学院工会和团委共同举办的首届&ldquo;光华杯&rdquo;迎春跑活动举行，活动以&ldquo;庆百年荣光，谱青春芳华&rdquo;为主题，将党史学习教育融入到春季户外跑步当中，号召全院师生以协力奔跑的形式献礼建党百年。在2021年的第100天，校内校外齐联动，天南地北共相约。光华管理学院副院长吴联生、张圣平、马力，党委副书记滕飞、团委书记鞠晓等50余名教职工通过校内、校外跑形式参与到活动中，近千名师生一起奔跑在神州大地的春光里。</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-04/1128db6f2efa4afd9074eab8eff31bb5.jpeg\" width=\"554\" height=\"370\" /></p>\n<p style=\"text-align: center;\">出发前的活动现场</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;迎春跑的校内活动起点设置在光华管理学院1号楼西侧&ldquo;敢当石&rdquo;前。上午8:00开始，陆续有师生来到此处报到、热身，随即开始3km的奔跑活动，更有老师和同学以党团支部、课题组、系所、班级、宿舍等为单位共同起跑，凝聚基层组织活力，共同庆祝建党百年。</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.pku.edu.cn/images/2021-04/eb4f6ae475764f4792a8e371ed26ded4.jpeg\" width=\"454\" height=\"303\" /></p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.pku.edu.cn/images/2021-04/04bd853805e74b96b2cc1ff4c3695fae.jpeg\" width=\"454\" height=\"303\" /></p>\n<p style=\"text-align: center;\">跑前共同合影</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&ldquo;重温党史&rdquo;主题学习展板依次放置于更衣区外的大厅走廊上。峥嵘岁月，逐梦前行。从党的一大召开到遵义会议确立毛泽东同志在党和红军中的领导地位，从中华人民共和国成立到改革开放，从中国加入世贸组织到全面建成小康社会，百年征程波澜壮阔，百年初心历久弥坚，参加活动的师生纷纷驻足，一起重温百年党史，不忘初心，牢记使命，奋进新时代，作出新贡献。</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.pku.edu.cn/images/2021-04/07cf784499544453954c46ddae6025ca.jpeg\" width=\"554\" height=\"280\" /></p>\n<p style=\"text-align: center;\">&ldquo;重温党史&rdquo;展板</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;校内活动的终点设在光华新楼，在终点，精心设计和准备的纪念T恤、奖牌、合影、盖有特制印章的纪念册让每一名参与者感受到了满满的成就感与获得感。师生们纷纷为迎春跑活动点赞&mdash;&mdash;&ldquo;在跑步过程中发现了许多未曾留意过的燕园美景，感受到了运动带来的释放和快意&rdquo;&ldquo;强身健体的同时和这么多光华人一起为建党100周年献礼，非常有意义&rdquo;&ldquo;参加迎春跑，是为了锻炼身体、丰富生活，更好地为祖国工作，这次活动参与的人很多，大家一起跑很有团体归属感。&rdquo;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.pku.edu.cn/images/2021-04/f8d0c42a988a49f380f03db4644ee1af.jpeg\" width=\"554\" height=\"370\" /></p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.pku.edu.cn/images/2021-04/9263b75f1f8b4896b628bab0a03f7dbb.jpeg\" width=\"554\" height=\"370\" /></p>\n<p style=\"text-align: center;\">师生跑步</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;校外活动部分与校内活动同步开始，4月10日至12日，在全国各地近30个城市，近400名师生参与了校外的&ldquo;云跑步&rdquo;，光华管理学院深圳分院、成都分院的师生们还自发组织了集体起跑。燕园内外，身处祖国各地的光华学子都用飞扬的脚步和蓬勃的朝气迎接春天的到来。</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.pku.edu.cn/images/2021-04/4aace09ccaa34f4592da380cb67e5b62.png\" width=\"454\" height=\"286\" /></p>\n<p style=\"text-align: center;\">校外跑活动：深圳分院班级同学同步开跑</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.pku.edu.cn/images/2021-04/0de304ab7bfc40e093ba73c07840734f.jpeg\" width=\"554\" height=\"370\" /></p>\n<p style=\"text-align: center;\">完成跑步后合影</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;春光正好，万物可亲。翰墨与弦歌之外，课堂与作业间隙，光华师生们奔跑于燕园春色之中，歌颂青春，献礼祖国。融党史学习于健身活动，为体育育人赋思想内涵，无论是昂扬奋进的参赛者，还是加油呐喊的志愿者，抑或是默默奉献的组织者，都在活动参与之中感受到了这次迎春跑的非凡意义。光华师生将进一步凝聚共识，砥砺奋斗，把党史学习教育同学院发展实际结合起来，以昂扬姿态奋力开启全面建设社会主义现代化国家新征程，以优异成绩迎接建党一百周年。</p>\n<p>&nbsp;</p>\n<p><a href=\"http://news.pku.edu.cn/ztrd/dsxxjy/index.htm\" target=\"%22%2522%252522%25252522_blank%25252522%252522%2522%22\"><img src=\"http://news.pku.edu.cn/images/2021-04/28d82a2af69f4a44836908d80937d86f.jpeg\" width=\"536\" height=\"58\" /></a></p>', 1, '北京大学新闻网', '北京大学新闻网', 5, 1, '2021-04-23 16:16:19', 1, '2021-04-23 21:46:29', 1, 1, '2021-04-23 16:16:19', NULL, 1, '2021-04-16 19:26:12', '2021-04-16 19:26:13', '谢志朋、钱六六、', '谢志朋、', 1, '2021-04-20 20:29:12', 1, '2021-04-20 20:32:45', 3, '2021-04-20 20:32:45', 1, '2021-04-23 16:16:19', '2021-04-23 16:15:00', 0, 'http://news.pku.edu.cn/images/2021-04/eb4f6ae475764f4792a8e371ed26ded4.jpeg', 0, '{\"CAROUSEL_IMAGE_URL\":\"http://news.pku.edu.cn/images/2021-04/04bd853805e74b96b2cc1ff4c3695fae.jpeg\"}');
INSERT INTO `news` VALUES (72, '深入推进“人才战略年”的各项工作——北京大学召开院长（系主任）行政月度会', '4月14日下午，北京大学在英杰交流中心阳光厅召开院长（系主任）行政月度会。会议聚焦“人才战略年”的各项重点任务，交流经验，明确任务，着力为“十四五”时期北大人才工作开好局。校长郝平，常务副校长龚旗煌，副校长王博、张平文、黄如，校长助理孙庆伟、董志勇等出席。各学部、院系、职能部门负责人参加。会议由龚旗煌主持。', NULL, 8, NULL, NULL, 5, 0, NULL, 0, NULL, 0, 0, NULL, 'http://news.pku.edu.cn/xwzh/3f1fb4b67c43428dba688ea34891484b.htm', 1, '2021-04-19 09:56:33', '2021-04-19 09:56:35', '谢志朋、', NULL, 1, '2021-04-23 21:07:36', 1, '2021-04-23 21:07:36', 1, '2021-04-23 21:07:36', 1, '2021-04-23 21:08:22', '2021-04-19 17:19:00', 0, 'http://news.pku.edu.cn/images/2021-04/61781799739543278301f35665ae8882.jpeg', 1, NULL);
INSERT INTO `news` VALUES (73, '蔡奇在北京大学主持召开市委教育工作领导小组会议', '3月24日上午，市委书记蔡奇到北京大学调研并主持召开市委教育工作领导小组会议。他强调，要坚持不懈以习近平新时代中国特色社会主义思想铸魂育人，落实立德树人根本任务，深化教育领域综合改革，建设高质量教育体系，推进教育治理体系和治理能力现代化，办好人民满意的首都教育，以优异成绩庆祝建党一百周年。市委副书记张延昆参加。', '<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;3月24日上午，市委书记蔡奇到北京大学调研并主持召开市委教育工作领导小组会议。他强调，要坚持不懈以习近平新时代中国特色社会主义思想铸魂育人，落实立德树人根本任务，深化教育领域综合改革，建设高质量教育体系，推进教育治理体系和治理能力现代化，办好人民满意的首都教育，以优异成绩庆祝建党一百周年。市委副书记张延昆参加。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;北京大学是中国传播马克思主义和建立北方地区中国共产党组织的最初基地。蔡奇走进校史馆，参观&ldquo;信仰百年，初心如一&mdash;&mdash;北京大学与中国共产党的建立&rdquo;专题展览和北京大学百年校史陈列展。一件件珍贵文物、一张张历史图片、一份份文献资料，记录了北京大学在马克思主义传播和中国共产党创建中特殊而重要的历史贡献，展示了中国共产党的主要创始人和早期著名活动家在北大工作学习、传播马克思主义和创建中国共产党早期组织的光辉历程。蔡奇一边听取讲解，一边询问细节，他说，展览史料翔实、内容丰富，对我们是一次深刻的党史学习教育，要从中汲取精神力量，把红色基因和革命传统传承好。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;随后，在北京大学召开市委教育工作领导小组会议。会议强调，要组织好教育系统党史学习教育，深入开展&ldquo;永远跟党走&rdquo;主题教育活动，确保覆盖到每一所学校、每一名师生。推进习近平新时代中国特色社会主义思想进教材、进课堂、进头脑，做好研究阐释，用党的创新理论武装师生。善用党史学习教育这一&ldquo;大思政课&rdquo;，推进大中小学思政课一体化建设，弘扬党的优良传统和精神谱系，厚植广大师生爱党爱国情怀。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;会议要求，坚持以政治建设为统领，把党的领导贯穿到办学治校各领域和全过程，把全面从严治党引向深入。要聚焦人民群众反映的突出问题，加强校外培训机构规范管理，优化学前教育资源布局，持续推动基础教育优质均衡发展，推进健康校园建设。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;会议要求，发挥教育系统科技人才优势，增强服务保障构建新发展格局的能力。办好高校高精尖创新中心，用好高校科技创新平台体系，打造国家战略科技力量。落实&ldquo;揭榜挂帅&rdquo;机制，瞄准关键核心技术开展重大项目研究，加强&ldquo;从0到1&rdquo;的基础研究和跨学科交叉研究。围绕国际科技创新中心建设、数字经济发展，提供人才和智力支撑。推进良乡、沙河高教园区建设，打造科教融合新城。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;会议要求，深化教育改革，坚持&ldquo;五育&rdquo;并举，推进市属高校分类发展。完善教师评价机制，把师德师风建设摆到突出位置，打造德才兼备的高素质教师队伍。要落实意识形态工作责任制，坚决维护教育领域安全稳定。毫不松懈抓好常态化疫情防控，加强平安校园建设。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;会议审议了市委教育工作领导小组2020年工作总结和2021年重点工作任务，听取了教育系统开展党史学习教育的汇报。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;北京大学和北京市领导邱水平、郝平、张家明、夏林茂、卢彦参加。</p>', 1, '北京大学新闻网', '北京大学新闻网', 5, 0, NULL, 0, NULL, 0, 0, NULL, NULL, 1, '2021-04-24 16:55:16', '2021-04-24 16:55:19', '谢志朋、', '谢志朋、', 1, '2021-04-24 17:54:45', 1, '2021-04-24 17:54:45', 1, '2021-04-24 17:54:45', 1, '2021-04-24 17:55:03', '2021-04-24 17:42:00', 3131, NULL, 0, NULL);
INSERT INTO `news` VALUES (74, '北京大学与新华社签署战略合作协议', '4月20日，北京大学与新华通讯社战略合作座谈会在英杰交流中心月光厅举行。新华通讯社社长、党组书记何平，北京大学党委书记邱水平、校长郝平参加相关活动。根据战略合作协议，双方将共建媒体融合生产技术与系统国家重点实验室，在科学技术、媒体及品牌活动、人才培养、智库建设与战略咨询等多方面展开长期紧密的战略合作。北京大学副校长王博主持座谈会和签约仪式。', '<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;4月20日，北京大学与新华通讯社战略合作座谈会在英杰交流中心月光厅举行。新华通讯社社长、党组书记何平，北京大学党委书记邱水平、校长郝平参加相关活动。根据战略合作协议，双方将共建媒体融合生产技术与系统国家重点实验室，在科学技术、媒体及品牌活动、人才培养、智库建设与战略咨询等多方面展开长期紧密的战略合作。北京大学副校长王博主持座谈会和签约仪式。</p>\n<p style=\"text-align: center;\"><img title=\"\" src=\"http://news.pku.edu.cn/images/2021-04/bec46a181c9c48cc91e009f07769a573.jpeg\" width=\"454\" height=\"303\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;4月20日，新华社社长、党组书记何平一行到访北京大学，北京大学与新华社签署战略合作协议。图为北京大学党委书记邱水平、校长郝平陪同何平参观北京大学校史馆。</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-04/293a4238599d4ce2a4c3ac96d7ace21b.jpeg\" width=\"454\" height=\"303\" /></p>\n<p style=\"text-align: center;\">会议现场</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;座谈会上，郝平对何平一行的来访表示热烈欢迎。他介绍了学校改革发展的总体情况和北京大学与新华社的合作情况。郝平指出，北京大学是我国新闻学和新闻教育的摇篮，新华社是中国共产党领导下成立最早的新闻机构，近年来，双方在人才培养、实习基地、智库建设、课题研究等方面广泛开展合作，取得了一系列丰硕成果。</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-04/af6b4b8ef6ba4418a85a73fb3674a695.jpeg\" width=\"454\" height=\"303\" /></p>\n<p style=\"text-align: center;\">郝平讲话</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;最后，郝平对双方合作建设媒体融合生产技术与系统国家重点实验室提出两点建议。一是深入推进媒体融合生产技术与系统国家重点实验室建设，凝聚北大智慧，助力主流媒体转型发展；二是切实加强干部和人才合作，为我国新闻传播事业的高质量发展提供坚强的人才支撑。郝平希望，双方以合作协议的签署为契机，共同推动新型主流媒体高质量、跨越式发展，为筑牢思想舆论阵地贡献更多力量。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;何平感谢长期以来北京大学对新华社的支持。他指出，新华社与北大有着深厚渊源，双方保持着良好合作关系。北大作为中国最高学府，新华社作为中国新闻界最高殿堂，双方开展战略合作，源于相同的红色基因、相融的历史记忆、相通的创新精神。今年是新华社建社90周年，此次战略合作协议的签署，是双方多年来合作的拓展和升级。何平希望新华社和北大未来在科技资源共享、智库合作、人才培养等方面深化合作，在报效祖国、服务人民的新征程上续写新的辉煌。</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-04/670d1838d9984e1bb2d1131a73c12c3f.jpeg\" width=\"454\" height=\"303\" /></p>\n<p style=\"text-align: center;\">何平讲话</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;新华社副社长刘思扬和北京大学副校长张平文代表双方签订战略合作协议。何平与郝平共同为&ldquo;媒体融合生产技术与系统国家重点实验室&rdquo;揭牌。</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-04/d3d560ff4a90447d82060b6c00c675c8.jpeg\" width=\"454\" height=\"260\" /></p>\n<p style=\"text-align: center;\">签约仪式现场</p>\n<p style=\"text-align: center;\"><img src=\"http://news.pku.edu.cn/images/2021-04/87fe6640556646e6b8cb61e267416823.jpeg\" width=\"454\" height=\"227\" /></p>\n<p style=\"text-align: center;\">何平与郝平共同为&ldquo;媒体融合生产技术与系统国家重点实验室&rdquo;揭牌</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;签约仪式前，何平一行参观了北京大学校史馆，观看了&ldquo;信仰百年，初心如一&mdash;&mdash;北京大学与中国共产党的建立&rdquo;专题展览和北京大学百年校史陈列展。在北京大学图书馆，何平一行参观了&ldquo;可爱的中国书&mdash;&mdash;中国书史展&rdquo;和大钊阅览室。何平一行还考察了北京大学大数据分析与应用技术国家工程实验室和北京大学前沿计算研究中心，认真听取相关负责人的技术汇报，并就共同感兴趣的问题进行了深入交流。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;出席活动的还有新华社秘书长宫喜祥，副秘书长兼人事局局长景如月，副秘书长兼办公厅主任朱海黎，总编室主任孙承斌，新媒体中心主任陈凯星，中国搜索董事长、总裁李俊，北京大学校长助理、秘书长、党办校办主任孙庆伟及北大相关职能部门、院系和科研机构负责人。</p>\n<p>&nbsp;</p>', 1, '北京大学新闻网', '北京大学新闻网', 5, 1, '2021-04-24 17:43:05', 1, '2021-04-24 17:43:06', 0, 0, NULL, NULL, 1, '2021-04-24 17:06:01', '2021-04-24 17:06:03', '谢志朋、', NULL, 1, '2021-04-24 17:34:26', NULL, NULL, 1, '2021-04-24 17:34:26', 1, '2021-04-24 17:43:06', '2021-04-24 17:42:00', 0, 'http://news.pku.edu.cn/images/2021-04/293a4238599d4ce2a4c3ac96d7ace21b.jpeg', 0, '{\"CAROUSEL_IMAGE_URL\":\"http://news.pku.edu.cn/images/2021-04/293a4238599d4ce2a4c3ac96d7ace21b.jpeg\"}');
INSERT INTO `news` VALUES (75, '李克强总理来南大考察侧记', '3月26日上午，中共中央政治局常委、国务院总理李克强来到南京大学仙林校区考察。了解基础研究情况，查看研究成果转化应用，鼓励科研人员加强跨领域协作，推动融合创新，他与学校院士们亲切交谈，希望一代一代传承推动创新。李克强十分关心毕业生就业，专门听取学校汇报，他与学生们交流，询问今年毕业学生的就业意向和签约情况。李克强勉励广大学生说，你们要记住南大校训中说的“诚朴雄伟”，要潜心向学，踏踏实实，从近...', '<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;3月26日上午，中共中央政治局常委、国务院总理李克强来到南京大学仙林校区考察。了解基础研究情况，查看研究成果转化应用，鼓励科研人员加强跨领域协作，推动融合创新，他与学校院士们亲切交谈，希望一代一代传承推动创新。李克强十分关心毕业生就业，专门听取学校汇报，他与学生们交流，询问今年毕业学生的就业意向和签约情况。李克强勉励广大学生说，你们要记住南大校训中说的&ldquo;诚朴雄伟&rdquo;，要潜心向学，踏踏实实，从近处、小处做起，将来实现远大志向，利社会，利国家。国务委员兼国务院秘书长肖捷、教育部党组书记、部长陈宝生，江苏省委书记娄勤俭，省长吴政隆等领导一同参加考察。校党委书记胡金波、校长吕建陪同考察调研。</p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image001_94014image001\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/20210329112337240hkawb4.jpg\" /></p>\n<p style=\"text-align: center;\"><strong>直击现场！</strong></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;26号上午10时许，在仙林校区电子科学与工程学院一楼大厅，校党委书记胡金波向总理介绍了南京大学基本情况，中国科学院院士、校长吕建，副校长王志林分别向李克强总理汇报了南京大学基础研究工作以及学生就业创业工作相关情况。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;南大作为中国高等教育第一方阵的重要一员，以基础研究、人才培养、科教报国著称。明年南大即将迎来建校120周年大庆。近120年来，南大出思想、出人才、出成果，走出了6位两弹一星元勋，5位国家最高科技奖获得者，270余位两院院士，充分体现了南大与时代同呼吸，与民族共命运、谋国家之强盛，求科教之进步的家国情怀。新时代南京大学将以基础研究为根，形成向创造技术、成果转化拓展延伸的三位一体原创驱动式科研新模式。近年来，南大坚持引导学生的就业志向和国家需求相结合，持续推进帮助学生就业的个性化工作机制，成效显著。</p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image003_53475image003\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/20210329112337447ryrxpk.png\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;祝世宁院士等向总理介绍了南京大学固体微结构物理国家重点实验室在光量子芯片、传感与成像等方向的最新科研工作进展。该实验室是我国第一批建立的10个国家重点实验室之一，相关研究曾获得国家自然科学一等奖（2006），国家自然科学二等奖9项。最近，实验室以曾获国家自然科学一等奖的光学超晶格材料为基础，制备了小型化、轻量化的高亮度偏振纠缠光源，搭载于自主研发的无人机上，完成了全球首个基于无人机的纠缠光子分发和光中继量子的纠缠分发实验，引发国内外广泛关注。总理饶有兴趣地听完介绍，并仔细观看了无人机现场实验的录像视频。</p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image005_95082image005\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/202103291123370183v2fhn.jpg\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;随后，总理参观了南京大学自旋电子学与超快光电能谱实验室。何亮教授介绍了该实验室拥有的世界首套自主设计、研发和搭建的飞秒级时间分辨及自旋分辨的电子能谱测量与先进材料生长联用系统，该系统各项技术指标已经达到了世界领先水平，将会对我国新一代自旋芯片的研发以及极深紫外光刻起到积极的推动作用。总理与方成、陈洪渊、王颖、符淙斌、郑有炓、吴培亨、邢定钰、祝世宁、沈树忠、邹志刚、郭子建、任洪强、洪银兴、赖永海、张志炳、施毅、李爱民、施斌等南京大学院士、人文资深教授和著名学者握手问好，愉快交流并合影留念。他说：&ldquo;你们为国家的科技事业、科技工作、科技成果付出了辛劳，做出了贡献，向你们表示敬意！南京大学名扬中外，在国内是一流大学，现正在向世界一流大学迈进，能走到今天，你们开创了很好的基础。&rdquo;</p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image007_95507image007\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/202103291123369169s5muz.jpg\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;校领导谈哲敏、王振林、陆延青等陪同参加了当天的考察活动。</p>\n<p style=\"text-align: center;\"><strong>&nbsp;&ldquo;欢迎总理！总理您好！&rdquo;</strong></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;南大校园两侧的林荫道上，闻讯而来的同学们用阵阵欢呼声和掌声热烈欢迎李克强总理来到南大。</p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image009_15133image009\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/202103291123368607cgbme.jpg\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;总理向同学们微笑挥手致意，和大家亲切攀谈。</p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image011_840image011\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/202103291123367708g6xkb.jpg\" /></p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image013_12849image013\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/20210329112336663xyzies.jpg\" /></p>\n<p style=\"text-align: center;\"><strong>盛赞南大：&ldquo;你们在中国的大学里，是走在最前列的！&rdquo;</strong></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;总理称赞道：&ldquo;南京大学是中国的一流大学，你们叫做第一方阵也好，叫做一流大学也好，反正你们是在中国我们的大学里，是走在最前列的，你们自己也应该为你们到南大感到自豪！&rdquo;</p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image015_69868image015\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/202103291123365884ijs58.jpg\" /></p>\n<p style=\"text-align: center;\"><strong>勉励学子：&ldquo;一定要诚心对待你想从事的事业！&rdquo;</strong></p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image017\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/20210329112336475h6jrdl.jpg\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&ldquo;我刚才一入校门的时候，看到你们的电子标牌上，写着诚朴雄伟......&rdquo;，总理话音未落，同学们便热切地接上下半句：&ldquo;励学敦行！&rdquo;&ldquo;还有一句是嚼得菜根&hellip;&hellip;&rdquo;&ldquo;做得大事！&rdquo;</p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image019\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/20210329112336420gt1jt7.jpg\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;看到南大的校训，总理笑着与同学们聊起校训中蕴含的哲理。总理说：</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;关于&ldquo;诚朴雄伟&rdquo;，&ldquo;你们应当有高尚的志向，有更远大的志向。但再有远大的志向，也要从近处做起，一定要诚心对待你想从事的事业！朴字意义就更广泛了，返璞归真，就得踏踏实实。诚朴雄伟，我理解就是沉下心来，踏踏实实追求你们更远大的理想和最终的成果，而且，这个成果可能不一定从你们自己的身上结出来，但是你们可能给下一代搭了人梯，得有这种诚心。&rdquo;</p>\n<p>关于&ldquo;嚼得菜根，做得大事&rdquo;，&ldquo;所谓大事，并不是高岗位，是这个事情对社会是有利的，要利天下，这应该是你们的志向。这样你们才能够真正地激发潜心向学的志向，做得大事。&rdquo;</p>\n<p style=\"text-align: center;\"><strong>点赞就业：&ldquo;让我们为南大骄傲！好不好！&rdquo;</strong></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;总理十分关心毕业生的就业情况，他询问道：&ldquo;你们的师兄（姐）们毕业情况怎么样？就业率高不高？&rdquo;在场同学们齐声答道：&ldquo;高！&rdquo;总理高兴地说：&ldquo;让我们为南大骄傲！好不好！&rdquo;&ldquo;好！&rdquo;现场掌声雷动。</p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image021\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/20210329113411523ppjcji.jpg\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;总理继续询问&ldquo;大家一起努力，你们也要提高自我竞争力。自己想去创业的有没有？举起手我看看。&rdquo;南大学子热烈回应！</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;总理最后寄语同学们，&ldquo;希望你们学有所成，学有所用，学有所为！&rdquo;</p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image023\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/202103291134114072d9rrw.jpg\" /></p>\n<p><img class=\"picshadow\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"image025\" src=\"https://news.nju.edu.cn/DFS//file/2021/03/29/20210329113411212cknjij.jpg\" /></p>', 1, '南京大学新闻网', '南京大学新闻网', 5, 1, '2021-04-24 17:52:44', 1, '2021-04-24 17:49:20', 0, 1, '2021-04-24 17:56:03', NULL, 1, '2021-04-24 17:08:54', '2021-04-24 17:08:56', '谢志朋、', NULL, 1, '2021-04-24 17:24:27', 1, '2021-04-24 17:24:27', 1, '2021-04-24 17:24:27', 1, '2021-04-24 17:49:20', '2021-04-24 17:48:00', 1313, 'https://news.nju.edu.cn/DFS//file/2021/03/29/20210329112337447ryrxpk.png', 1, '{\"CAROUSEL_IMAGE_URL\":\"https://news.nju.edu.cn/DFS//file/2021/03/29/20210329112337447ryrxpk.png\"}');
INSERT INTO `news` VALUES (76, '浙江大学艺术与考古学院首届教师中国书画作品展开幕', '4月22日，浙江大学艺术与考古学院首届教师中国书画作品展开幕式在浙江大学图书馆古籍馆举行，浙江大学党委书记任少波出席并宣布展览开幕。展厅中，绘画作品墨妙如生，书法作品厚重古拙，篆刻作品沉浑奇崛，15位学院教师创作的50余件展品，让观展者感受到浓厚的艺术气息和文化感染力。', '<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://www.news.zju.edu.cn/_upload/article/images/63/93/12a3161946aaa1092236517023df/a68c12fc-8370-41fc-a3e8-185d3afb81fa.jpg\" data-layer=\"photo\" /></p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://www.news.zju.edu.cn/_upload/article/images/63/93/12a3161946aaa1092236517023df/f6bfc92a-bf2c-4d5e-94e7-16ba88299720.jpg\" data-layer=\"photo\" /></p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://www.news.zju.edu.cn/_upload/article/images/63/93/12a3161946aaa1092236517023df/8bde1376-d906-4a81-8032-0c664b1122ed.jpg\" data-layer=\"photo\" /></p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;4月22<span lang=\"zh-CN\">日，浙江大学艺术与考古学院首届教师中国书画作品展开幕式在浙江大学图书馆古籍馆举行，浙江大学党委书记任少波出席并宣布展览开幕。</span></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;展厅中，绘画作品墨妙如生，书法作品厚重古拙，篆刻作品沉浑奇崛，15位学院教师创作的50余件展品，让观展者感受到浓厚的艺术气息和文化感染力。</p>\n<p><span lang=\"zh-CN\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;现场，党委副书记邬小撑为学院池长庆教授颁发捐赠证书。在学校建校一百二十四周年之际，由池长庆创作的巨幅大字作品《浙大校歌（马一浮词）》将捐赠给学校。这幅作品长</span>21余米，高近8米，由156幅单字作品组成，用魏碑体书写了由马一浮先生创作于1938<span lang=\"zh-CN\">年的浙大校歌歌词，是目前为止作者创作的最大的一幅书法作品。</span></p>\n<p><span lang=\"zh-CN\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;据悉，此次展览为期一个月，是浙江大学艺术与考古学院自</span>2019<span lang=\"zh-CN\">年成立以来，教师中国书画作品的首次集中展示，涵盖的风格、样式与创作观念十分广泛，旨在反映学院创作面貌，听取校内外专家与爱好者的批评，凸显学校美育成果，推动教育教学水平的提升。</span></p>\n<p><span lang=\"zh-CN\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;参展教师表示，艺术关乎自我修养，也关乎社会风气与文化自信，在综合性大学中，艺术相关专业能发挥将文化和美学思想具体化和形象化的优势，培育高尚品格的学子，并以此为根基向社会渗透辐射。前来观展的师生说，在作品中感受到中国传统文化艺术之美，提升了自身的审美情趣和鉴赏能力。</span></p>\n<p><span lang=\"zh-CN\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;浙江大学发展委员会副主席郑造桓、卜凡孝，来自浙江省书法家协会、中国美术学院、浙江省美术家协会、浙江美术馆、杭州画院、杭州美术家协会、广宇集团的嘉宾和浙江大学有关部门负责人参加了开幕式。</span></p>\n<p style=\"text-align: right;\"><span lang=\"zh-CN\">（文 周伊晨</span><span lang=\"en-US\">/</span><span lang=\"zh-CN\">摄影 朱原之）</span></p>\n<p><span lang=\"zh-CN\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;新闻链接：</span><a href=\"http://www.news.zju.edu.cn/2021/0423/c23267a2316769/page.htm#p=1\" target=\"_self\">浙江大学艺术与考古学院首届教师中国书画作品展（部分展出作品）</a></p>\n<p>&nbsp;</p>', 1, '浙江大学新闻网', '浙江大学新闻网', 5, 0, NULL, 1, '2021-04-24 17:46:05', 0, 0, NULL, NULL, 1, '2021-04-24 17:26:36', '2021-04-24 17:26:38', '谢志朋、', NULL, 1, '2021-04-24 17:34:22', NULL, NULL, 1, '2021-04-24 17:34:22', 1, '2021-04-24 17:46:05', '2021-04-24 17:43:00', 3131, 'http://www.news.zju.edu.cn/_upload/article/images/63/93/12a3161946aaa1092236517023df/8bde1376-d906-4a81-8032-0c664b1122ed.jpg', 0, NULL);
INSERT INTO `news` VALUES (77, '搜狗捐赠清华大学 支持计算机领军人才队伍建设', '4月22日，在清华大学110周年校庆来临之际，搜狗公司宣布向清华大学教育基金会下设的计算机系发展基金捐赠，与计算机系共同设立领军人才奖励项目，用于支持计算机系高层次领军人才队伍的建设。', '<p class=\"vsbcontent_start\"><strong>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;清华新闻网4月24日电</strong>&nbsp;4月22日，在清华大学110周年校庆来临之际，搜狗公司宣布向清华大学教育基金会下设的计算机系发展基金捐赠，与计算机系共同设立领军人才奖励项目，用于支持计算机系高层次领军人才队伍的建设。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;捐赠仪式在清华大学工字厅举行。清华大学计算机系1996级校友、搜狗CEO王小川，1998级校友、搜狗CTO杨洪涛，搜狗CFO周雯娟，电子工程系2002级校友、搜狗高级副总裁许静芳；清华大学副校长、教育基金会理事长杨斌，中国工程院院士、清华大学计算机系教授吴建平，人事处处长王宏伟，计算机系主任尹霞，计算机系党委书记刘奕群，计算机系副书记贾珈等出席捐赠仪式。清华大学教育基金会秘书长袁桅主持仪式。捐赠仪式结束后，清华大学党委书记陈旭会见了王小川校友和搜狗科技团队。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"\" src=\"https://www.tsinghua.edu.cn/__local/0/72/71/E1CC0A121942A2F9B384AAE035B_7DDAD4E6_253380.jpg\" width=\"554\" /></p>\n<p style=\"text-align: center;\">王小川致辞</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;王小川在致辞中表示，清华的读书时光为他的整个人生道路打下了非常坚实的基础，计算机系赋予他的创新创业精神奠定了他事业的发展方向。搜狗与清华的渊源颇深，自成立起就一直扎根在距离母校仅几步之远的清华科技园，多年来的发展壮大更是离不开母校的人才支持，搜狗现任CEO、CMO、CTO等高管均为清华校友，不仅如此，创业初期的搜狗团队高管也全部来自清华，清华的优秀人才源源不断地为搜狗注入了新鲜血液与技术活力。王小川认为，校企深度合作既能助力校方的前沿研究，同时也有利于企业的创新发展，是一种双赢的良性发展；而作为清华人，他也希望通过此次捐赠来回馈母校，支持计算机系高层次领军人才队伍的建设，助力计算机学科延揽世界级大师，取得更多引领性的学术研究。最后，他祝福母校110周年生日快乐，同时祝愿计算机系蒸蒸日上。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.tsinghua.edu.cn/__local/F/8B/23/A297B874130B3631B25EA5BBBCC_33FECDC9_272134.jpg\" width=\"554\" /></p>\n<p style=\"text-align: center;\">杨斌致辞</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;杨斌首先代表学校感谢王小川校友和搜狗科技的慷慨捐赠。他提到，在清华大学110岁生日来临之际，王小川校友的捐赠不仅表达了对于母校的深情厚意，同时也彰显了清华人才培养的成绩，可谓&ldquo;喜上加喜&rdquo;。清华有很多像王小川一样的杰出校友，虽已毕业，但他们和母校及院系之间却始终紧紧相依。王小川作为计算机系的学长，一直在关爱和提携清华的学弟学妹们，这对于清华学子而言是极好的榜样。杨斌表示，习近平总书记在前几天考察了清华大学，对学校培养一流顶尖人才寄予了殷切期望，如何更好地鼓励科研创新和领军人才培养，学校还有很多值得进一步思考及长足发展的空间，同时要保持自信、认可我们的教育，努力为社会发展贡献力量。杨斌还肯定了计算机系在建设一流的道路上积极募集社会资源助力学科发展的长远目光，并表示搜狗科技的捐赠将为清华教师和人才队伍的建设提供有益的支撑。最后，杨斌祝愿搜狗公司&ldquo;百尺竿头、更进一步&rdquo;。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.tsinghua.edu.cn/__local/4/D6/0B/36AF38AF0994AD86083E6328F2E_BFFDE21F_240910.jpg\" width=\"554\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;吴建平致辞</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;吴建平在致辞中感谢了王小川校友不忘初心、回馈母校的情怀。他提到，王小川在学生时代就表现出优秀的创业精神，后来他一路成长，担任过搜狐最年轻的副总裁，又一手创办了搜狗并成功带领企业赴美上市。尤为难得的是，从支持新系馆建设，到连续十多年独家赞助智能体大赛，到关心系里学弟学妹们的职业成长，再到捐赠活动基金支持学生文体活动，王小川校友一直心系计算机系的发展。在产学研合作方面，搜狗与清华联合建立的天工智能研究院，这些年来在自然语言处理、信息检索、人机交互等方面开展合作课题，形成了一系列高水平研究成果并发表在国际顶级会议上，产生了较大的国际影响力，相关专利和成果也被应用在搜狗的搜索、输入法、翻译等应用场景中，服务于广大用户，为社会创造了价值。他表示，后疫情时代，世界迎来了&ldquo;百年未有之大变局&rdquo;，计算机学科的发展也面临着前所未有的机遇和挑战，因此建设一支世界一流前列水准的领军人才队伍对于计算机学科而言尤为重要。站在110周年校庆这个承上启下的时刻，相信在学校强有力地领导下，在搜狗科技和王小川校友一如既往地鼎力支持下，在计算机系新一届领导班子的努力奋斗下，计算机系必将不辱使命，续写新的绚丽篇章。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.tsinghua.edu.cn/__local/B/E7/D4/942733AD70216E7F4A90D7ED823_3EE36E5F_267F2E.jpg\" width=\"554\" /></p>\n<p style=\"text-align: center;\">王宏伟致辞</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;王宏伟分享了习近平总书记视察清华时的指示，教师要成为&ldquo;大先生&rdquo;，做学生为学、为事、为人的示范，促进学生成长为全面发展的人。他提到，学校设立准聘长聘制度，推行讲席（冠名）教授制度等一系列教师人事制度改革举措，为优秀学者安心从事教学科研工作提供了具有长远意义的保障。他非常感谢搜狗科技对清华大学师资队伍建设的支持，并期待未来清华和校友企业携手共进，共同打造具有一流国际竞争力的教师队伍，为培养社会主义建设者和接班人、服务国家经济社会发展奠定坚实基础。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.tsinghua.edu.cn/__local/C/C5/A8/3513B5DE4ADF1CD0941BFE8DDAD_2D6C4B6E_23A561.jpg\" width=\"554\" /></p>\n<p style=\"text-align: center;\">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;尹霞致辞</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;尹霞在讲话中提到，搜狗科技与计算机系渊源深厚，长期以来，搜狗一直积极支持计算机系的发展建设，这让她倍受感动。计算机系在过去60多年的奋进历程中，始终以发展我国计算机科学研究事业、培养国家计算机高层次人才为己任，承担了一大批国家科研项目，培养了近2万名英才。作为同时享有国际声誉和国内领先地位的教学科研机构，未来，清华大学计算机系有信心，在学校的领导下，在搜狗科技的倾力支持下，将计算机学科建设成为引进和培养全球顶尖计算机专业人才的平台，成为世界一流前列的计算机专业本科生和研究生的培养基地，成为世界先进、国内领先的计算机科学与技术高水平研究基地，为中国和世界的计算机科学与技术的进步作出新的、更大的贡献。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.tsinghua.edu.cn/__local/1/4D/4C/A87A0351C676A468D46BFD18652_15AEA309_2BEF55.jpg\" width=\"554\" /></p>\n<p style=\"text-align: center;\">王小川与袁桅签署捐赠协议</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.tsinghua.edu.cn/__local/0/95/34/9E61CC2D28AD3B0A98FEDECD3DE_564C993A_296151.jpg\" width=\"554\" /></p>\n<p style=\"text-align: center;\">杨斌向王小川颁发捐赠纪念牌</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;仪式上，王小川与袁桅签署捐赠协议，杨斌向王小川颁发捐赠纪念牌。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://www.tsinghua.edu.cn/__local/9/DA/E1/0F08DF3D7609CE9CEB78C99ABCD_4B2196C4_405E1B.jpg\" width=\"554\" /></p>\n<p style=\"text-align: center;\">合影</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;捐赠仪式结束后，陈旭会见了王小川校友和搜狗科技团队，并同大家亲切交流。陈旭对搜狗科技长期以来对清华大学的支持表示衷心的感谢，她表示，此次捐赠将对学校高层次人才和师资队伍的建设起到良好的支持作用。王小川和搜狗科技团队表示，今后搜狗将继续深化与清华大学的合作，为母校增色添彩，也为社会贡献更多的&ldquo;搜狗科技力量&rdquo;。</p>\n<p style=\"text-align: right;\">供稿：基金会</p>\n<p style=\"text-align: right;\">编辑：张同顺</p>\n<p style=\"text-align: right;\">审核：吕婷</p>', 1, '清华大学新闻网', '清华大学新闻网', 5, 1, '2021-04-24 17:52:24', 1, '2021-04-24 17:51:23', 0, 1, '2021-04-24 17:56:20', NULL, 1, '2021-04-24 17:29:08', '2021-04-24 17:29:10', '谢志朋、', NULL, 1, '2021-04-24 17:34:17', NULL, NULL, 1, '2021-04-24 17:34:17', 1, '2021-04-24 17:47:52', '2021-04-24 17:47:00', 3134, 'https://www.tsinghua.edu.cn/__local/1/4D/4C/A87A0351C676A468D46BFD18652_15AEA309_2BEF55.jpg', 7, '{\"CAROUSEL_IMAGE_URL\":\"https://www.tsinghua.edu.cn/__local/1/4D/4C/A87A0351C676A468D46BFD18652_15AEA309_2BEF55.jpg\"}');
INSERT INTO `news` VALUES (78, '习近平总书记致信祝贺厦门大学建校100周年 庆祝厦门大学建校100周年大会隆重举行', '4月6日，厦门大学迎来百年华诞。中共中央总书记、国家主席、中央军委主席习近平致信祝贺厦门大学建校100周年，向全体师生员工和海内外校友致以热烈祝贺和诚挚问候。习近平总书记在贺信中指出，厦门大学是一所具有光荣传统的大学。100年来，学校秉持爱国华侨领袖陈嘉庚先生的立校志向，形成了“爱国、革命、自强、科学”的优良校风，打造了鲜明的办学特色，培养了大批优秀人才，为国家富强、人民幸福和中华文化海外...', '<div id=\"vsb_content\" class=\"chapter\">\n<div class=\"v_news_content\">\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"\" src=\"https://news.xmu.edu.cn/__local/1/98/C6/B53D5E520327909DFF9F3043D8E_B359C4F0_34F72.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;弘扬嘉庚精神，奋进一流征程。4月6日，厦门大学迎来百年华诞。中共中央总书记、国家主席、中央军委主席习近平致信祝贺厦门大学建校100周年，向全体师生员工和海内外校友致以热烈祝贺和诚挚问候。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;习近平总书记在贺信中指出，厦门大学是一所具有光荣传统的大学。100年来，学校秉持爱国华侨领袖陈嘉庚先生的立校志向，形成了&ldquo;爱国、革命、自强、科学&rdquo;的优良校风，打造了鲜明的办学特色，培养了大批优秀人才，为国家富强、人民幸福和中华文化海外传播作出了积极贡献。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;习近平总书记强调，我国已开启全面建设社会主义现代化国家新征程。希望厦门大学全面贯彻党的教育方针，切实落实立德树人根本任务，为党育人、为国育才，与时俱进建设世界一流大学，全面提升服务区域发展和国家战略能力，为增强中华民族凝聚力和向心力，为全面建设社会主义现代化国家、实现中华民族伟大复兴的中国梦作出新的更大贡献。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;中共中央政治局委员、国务院副总理孙春兰向厦门大学建校100周年表示祝贺，就学习贯彻习近平总书记贺信精神作出批示。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/5/B4/D0/63E8E476D3EF685D924CDFF4935_3ABE27E3_36FA8.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;6日的厦大校园，到处洋溢着节日的气氛。上午8时30分，庆祝厦门大学建校100周年大会在建南大会堂隆重举行。全校师生员工、海内外校友和关心厦大发展的各界人士相聚线下线上，共襄盛举，见证厦门大学百年荣光。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;原国务委员、第十一届全国人大常委会副委员长 陈至立，教育部党组书记、部长陈宝生，福建省委书记、省人大常委会主任尹力，省委副书记、省长王宁，省政协主席崔玉英，第十三届全国人大外事委员会副主任委员刘赐贵，中国社科院原党组副书记、常务副院长，厦门大学原党委书记王洛林，台盟中央常务副主席、第十三届全国人大常务委员会委员李钺锋，福建省委常委周联清，福建省委常委、厦门市委书记赵龙，福建省委常委、福州市委书记林宝金，北京大学校长郝平，陈嘉庚长孙陈立人，在主席台前排就座。校党委书记张彦主持庆祝大会。</p>\n<p>大会在庄严的国歌声中开始。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/3/D5/AD/5CDC6EBA1A9617E3848441833F4_9835E3D1_1E8ED.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;陈宝生宣读习近平总书记贺信并致辞。陈宝生强调，习近平总书记的贺信充分肯定了厦门大学百年来的办学成就，对学校未来发展寄予殷切期望，提出明确要求，充分体现了以习近平同志为核心的党中央对厦门大学的高度重视和亲切关怀，为学校在新起点上实现高质量发展，指明了前进方向，提供了根本遵循。他指出，建校百年，厦大始终秉承&ldquo;自强不息、止于至善&rdquo;校训精神，与祖国同呼吸，与民族共命运，不忘初心，砥砺前行，为国家富强、民族复兴、社会进步作出了应有贡献。学校要深入贯彻落实习近平总书记贺信精神，坚定方向，扎根中国大地办大学，全面贯彻党的教育方针，坚持和加强党的全面领导，把党的政治建设摆在首位，通过开展党史学习教育，从党的百年历史中汲取前进智慧和力量，把学习成效转化为推动学校发展的强大动力。坚守初心，落实立德树人根本任务，坚守为党育人，为国育才，大思政课要善用之，加快构建高质量人才培养，努力提高落实立德树人根本任务的质量和成效。勇担使命，增强服务创新发展能力，聚焦国家战略需要，加快基础学科转型，努力破解&ldquo;卡脖子&rdquo;问题，深入融入新福建建设，更好服务国家和区域经济社会发展，奋力推进中国特色世界一流大学建设，为建设教育强国，全面建设社会主义现代化国家作出新的更大贡献。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/4/E9/F0/1B2928F16CD771B0F6025071478_0444F2D6_23824.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;校长张荣作题为《建世界之大学 为吾国放异彩》的致辞。他表示，厦门大学的百年史，就是一部兴学图强的爱国史、自强不息的奋斗史。百年的求索与开拓、苦难与辉煌，都深深内化为厦大人心中以&ldquo;嘉庚精神&rdquo;为源流，以&ldquo;自强不息、止于至善&rdquo;校训为精髓，以&ldquo;爱国、革命、自强、科学&rdquo;&ldquo;四种精神&rdquo;为内核，以&ldquo;感恩、开放、创新、和谐&rdquo;文化特质为品格的精神坐标。百年厦大，坚守的是志怀祖国、希图报效的家国情怀，是百折不挠、敢拼会赢的自强风骨，是致知无央、走向卓越的至真追求，是博集东西、造福世界的开放胸襟，是充爱无疆、衔环涌泉的感恩传承。回望百年的办学历程，我们更加深刻地认识到，无论时代如何变化，大学的成长壮大离不开国家民族的繁荣昌盛和文明进步，大学的常新长青离不开历久弥坚的文化根脉和精神追求，大学的提升超越离不开与时俱进的使命担当和开拓创新。百年厦大，得到了各方无微不至的关心和帮助。党和国家几代领导人先后莅校视察或接见我校师生代表，给予厦门大学亲切关怀；教育部和国家其他部委高度重视厦大建设发展，给予学校大力支持和悉心指导；福建省、厦门市始终对厦大厚爱有加，与教育部长期重点共建厦门大学，给予学校源源不断的投入；许许多多关心和爱护厦门大学的华侨华人、各界贤达慷慨解囊、捐资兴学，鼎力支持厦大建设与发展；千千万万厦大校友热心母校事业发展，成为厦大发展的重要参与者、推动者和建设者。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;张荣表示，百年华诞，是厦门大学办学进程中的重要里程碑，更是学校迈向新百年的历史新起点。立足新时代，面向新百年，厦门大学必须以更高远的历史站位、以更深邃的战略眼光，仰望星空，脚踏实地，从中国的伟大实践中汲取前行的力量。他强调，胸怀&ldquo;两个大局&rdquo;，心系&ldquo;国之大者&rdquo;，增强&ldquo;四个意识&rdquo;、坚定&ldquo;四个自信&rdquo;、做到&ldquo;两个维护&rdquo;，坚守立德树人根本任务，强化人才培养第一责任，为党育人、为国育才，成为国家核心利益和民族长远利益的坚定维护者和积极贡献者；矢志世界一流，彰显&ldquo;国之大学&rdquo;，把服务国家战略和社会进步作为建设一流大学的高度自觉，在服务发展中创造更多一流成果，坚持问学东西、融通中外，积极在世界高等教育舞台上展现作为，与世界一流大学相颉颃；强化创新引领，锻造&ldquo;国之大器&rdquo;，坚持&ldquo;四个面向&rdquo;，聚焦关键核心技术和&ldquo;卡脖子&rdquo;问题，深化科教融合、产教融合，建设新型研究型大学，打造科技创新平台，建设高端智库，提升国家自主创新能力；坚守至善理想，弘扬&ldquo;国之大道&rdquo;，坚定文化自信，推动中华优秀传统文化创造性转化、创新性发展，当好文明交流的使者，架设文明互鉴的桥梁，为应对人类共同挑战、构建人类命运共同体作出厦大贡献。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/4/EC/FC/5F59C664643AA4F715F684CD08B_425906BE_20CFC.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;兄弟高校代表、北京大学校长郝平在致辞中表示，一个世纪以来，厦门大学已经发展成为一所具有广泛国际影响力和崇高学术声誉的著名高校。自建校起，厦门大学就与北京大学结下了不解之缘，具有共通的精神基因和光荣传统。新中国成立后，两校继续携手奋进，共同助力国家建设和发展。近年来，两校以习近平新时代中国特色社会主义思想为指引，主动服务国家战略，携手组建&ldquo;21世纪海上丝绸之路&rdquo;大学联盟，在人才培养、科研攻关等领域全面深化合作，并取得了丰硕成果。相信北大和厦大一定会在全面建设社会主义现代化国家的新征程上继续砥砺同行，厦门大学一定能够传承百年荣光，与全国兄弟高校共同奋斗，为实现中华民族的伟大复兴谱写新的乐章。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;境外友好高校代表、纽约大学校长安德鲁&middot;汉密尔顿、香港大学校长张翔通过视频致辞，祝愿厦门大学明天会更好，期待与厦大开展更为紧密务实的合作，为学生开拓全球视野，走向国际舞台，成为具有责任感、公德心与国际竞争力的世界公民而努力。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/5/40/4C/BA489642650D664F3910E5E7118_4DA187A8_1CDB9.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;陈立人在致辞中表示，厦门大学是祖父陈嘉庚先生倾注心血最多，寄托最大希望的一所学校。作为嘉庚先生的后裔，自己一直很关心关注厦大的发展。近几年，看到厦大迅速发展和卓越成就，看到学校领导、师生员工昂扬向上、奋力拼搏的精神面貌，看到厦大在海外华侨华人中和国际上更加广泛的影响力，看到马来西亚分校良好的发展势头，深感无比自豪。迈入新百年，相信厦门大学的各项事业会更上一层楼，必将在不久的将来成为一所著名世界一流大学，实现陈嘉庚先生立下的建设&ldquo;世界之大学&rdquo;的志向。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/D/75/72/9E8167E824895CE7DCD4BBD2BDE_DA3230B3_1DA02.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;南强杰出贡献奖是厦门大学授予教师的最高荣誉，用以奖励为学校建设和发展作出巨大贡献的优秀教师，迄今为止已连续颁发9届，共有28位厦大教师获此殊荣。在全场热烈的掌声中，陈至立为田昭武、黄本立、陈孔立、吴宣恭、林祖赓、洪华生、张亦春、吴伯僖、潘维廉、赵玉芬、郑学檬、夏宁邵、郑兰荪、田中群等14位获奖代表颁授&ldquo;厦门大学南强杰出贡献奖章&rdquo;。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/8/15/30/D3BA484EBD18AD846DC3537B88D_49304B41_1A6B1.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;校友代表、清华大学文科资深教授、1978级哲学系谢维和在发言中说，在校友心里，母校校园永远难以忘怀，母校的师长们永远是最尊敬的恩人，大学同窗永远是难忘的兄弟姐妹。全体校友与母校荣辱与共、风雨同舟，期待与母校领导、老师和同学们一同为母校新百年更上一层楼、创造新辉煌贡献一份力量。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/A/7B/CD/2E251C13BF6313CB18B50C0E7F9_7AC3F24C_1C6A2.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;教师代表、中国科学院院士、厦门大学化学化工学院孙世刚表示，教师是太阳底下最光辉的职业，是值得我们终身奉献的事业，要充分珍惜这份信任和荣誉，当嘉庚精神的&ldquo;诠释者&rdquo;，做学生成长的&ldquo;引路人&rdquo;，强国之梦的&ldquo;筑梦人&rdquo;。新百年的奋进之篇已经展开，作为教师队伍的一员，将继续保持对教育的热情和激情，以学生为中心，实现&ldquo;教育增值&rdquo;最大化，以赤诚之心、奉献之心、仁爱之心投身学校的教育事业。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;博伊特勒书院理事长、诺贝尔生理学或医学奖获得者布鲁斯&middot;博伊特勒表示，厦门大学博伊特勒书院开办以来，自己越来越为厦大学生的能力和取得的成就感到骄傲自豪。成功不止需要丰富知识，更需要正确心态，培养学生并非简单指导，教师们需要作出榜样、形成合力、付出努力。相信未来厦大一定能够培养出更多优秀人才。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/D/4B/46/75AAD4845EBCD143F571B5F1D2A_FD13A545_1BA2C.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;学生代表、管理学院2019级本科生彭派回忆自己在校期间两年的军旅生涯，向学校送上生日祝福。她说，年轻学子要永远听党话，坚定跟党走，将小我融入大我，把青春献给祖国，勇做走在时代前列的奋进者、开拓者、奉献者，在实现民族复兴、人类进步伟大征程中书写无愧于时代的青春华章。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/D/9D/14/45DAD071A01A651A40BCC9EC06D_C7ACED9E_19FB1.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;赵龙代表厦门市委市政府向厦门大学全体师生员工致以热烈祝贺和诚挚问候。他指出，100年来，厦大与厦门相生共荣，相得益彰。厦门滋养了厦大，厦大反哺了厦门，厦大从厦门走向世界。这份校地情缘值得倍加珍惜。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;赵龙表示，站在历史新起点上，希望厦门大学传承红色基因，不忘初心，牢记使命，始终高举立德树人旗帜，大力弘扬嘉庚精神，不忘&ldquo;自强不息、止于至善&rdquo;校训，持续加强新时代思想政治工作，持续加强基础研究和科技创新，持续发挥&ldquo;侨、台、特、海&rdquo;优势，持续深化部省市校合作，用学科优势赋能福建，赋能厦门重点产业的发展，助力福建、厦门推进金砖国家新工业革命伙伴关系创新基地等&ldquo;五中心一基地&rdquo;建设，更好地服务全国、服务全省的大局。厦门市委市政府将一如既往支持厦门大学的发展，全面落实部省市共建机制，为学校建设、人才培养、科研成果转化落地等创造有利条件，尽最大努力与厦大一道，把学校办得更好，让百年的校地情缘历久弥新，结出更加丰硕成果。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/6/3D/73/DD463A9877F856CBC2EBEB30EB4_97F4B44B_1963B.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;尹力代表福建省委省政府向厦门大学百年校庆表示热烈祝贺。他指出，党中央高度重视厦门大学的发展建设，习近平总书记专门发来贺信，充分肯定了百年厦大作出的突出贡献，对厦大发展提出殷切希望，充分体现了对厦门大学的亲切关怀，对教育事业的高度重视，给我们巨大的鼓舞和鞭策。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;尹力表示，厦门大学具有光荣的革命传统，是福建省第一个中共党支部的诞生地，有着丰富的历史积淀和宝贵的精神财富。厦大立足福建，服务全国，为国家，特别是福建的经济社会发展、文化繁荣进步作出了重要的贡献。厦大与福建水乳交融、风雨同舟、共荣共享，福建为有厦门大学倍感骄傲和自豪。希望厦门大学按照习近平总书记贺信要求，坚持党的教育方针，肩负起为党育人、为国育才的历史使命，坚持立德树人，把学校发展与国家发展、福建发展紧密结合起来，培养更多的高素质人才，推出更多的高质量科研成果，增强国际交流与合作，努力建设中国特色、世界一流的大学。福建省委、省政府将一如既往关心、支持学校建设发展，为广大师生创造更加良好的条件，让厦大的学子在八闽大地上施展才华、创造价值、实现梦想。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/3/BD/78/D391A8609059EC3454BE539B258_BA8BB52C_34A87.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;长期以来，教育部、福建省和厦门市始终高度重视、全力支持厦门大学的建设发展。进入新世纪以来，先后4次签署重点共建厦门大学协议书，为厦门大学全面建成世界知名高水平研究型大学，实现第一个百年奋斗目标提供了良好发展环境和强有力支撑。庆祝大会上，举行了《教育部、福建省人民政府、厦门市人民政府继续重点共建厦门大学协议书》签约仪式。</p>\n<p><img class=\"img_vsb_content\" style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://news.xmu.edu.cn/__local/6/46/3E/495B1AA29C4ABA7446352B72736_3BEB5DEB_14DCD.jpg\" width=\"700\" /></p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;张彦在主持大会时表示，习近平总书记长期关心关怀厦门大学，党中央始终对厦门大学发展给予高度重视。令厦大人难以忘怀的是，习近平同志曾出席厦门大学建校 80 周年庆祝活动，勉励学校大力弘扬嘉庚精神，努力把厦大办得更好，不负先贤苦心；在建校 90 周年之际专门发来贺信，希望学校继续弘扬嘉庚精神，牢记办学使命，做出更大贡献。在建校100周年的重要时刻，习近平总书记再次发来贺信，充分体现了党中央对高等教育、对厦门大学的高度重视和殷切期望，为学校开启新百年征程指明了方向，提供了根本遵循，全体师生倍感振奋、倍受鼓舞。厦门大学将把学习贯彻总书记的贺信精神作为当前和今后一个时期的首要政治任务，同学习贯彻习近平新时代中国特色社会主义思想紧密结合起来，同融入国家&ldquo;四点一线一面&rdquo;教育现代化战略布局紧密结合起来，同服务福建全方位推动高质量发展超越紧密结合起来，抢抓机遇，乘势而上，书写高等教育&ldquo;奋进之笔&rdquo;的厦大篇章，努力开创建设中国特色世界一流大学的新局面。</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;国家部委、省市各级领导，兄弟高校、战略合作单位嘉宾，院士专家，海内外校友，捐赠嘉宾，社会各界人士，厦大师生代表等3千余人参加了大会。</p>\n<p style=\"text-align: right;\">（宣传部&nbsp; 文\\陈浪&nbsp; 图\\潘万华&nbsp;庄华）</p>\n<p>&nbsp;</p>\n</div>\n</div>\n<div class=\"editor\" style=\"text-align: right;\">【责任编辑：陈联文】</div>', 1, '厦门大学新闻网', '厦门大学新闻网', 5, 1, '2021-04-24 17:52:58', 1, '2021-04-24 17:46:56', 0, 1, '2021-04-24 17:56:22', NULL, 1, '2021-04-24 17:32:04', '2021-04-24 17:32:06', '谢志朋、', NULL, 1, '2021-04-24 17:34:19', NULL, NULL, 1, '2021-04-24 17:34:19', 1, '2021-04-24 17:46:56', '2021-04-24 17:46:00', 0, 'https://news.xmu.edu.cn/__local/1/98/C6/B53D5E520327909DFF9F3043D8E_B359C4F0_34F72.jpg', 4, '{\"CAROUSEL_IMAGE_URL\":\"https://news.xmu.edu.cn/__local/1/98/C6/B53D5E520327909DFF9F3043D8E_B359C4F0_34F72.jpg\"}');
INSERT INTO `news` VALUES (79, '校领导率队赴南京参会并调研兄弟高校', '4月18至20日，副校长刘小丽率队参加在南京市举办的第十届EGPC全球合作伙伴会议和2021来华留学高层论坛，并在会后赴河海大学、南京信息工程大学就国际化各项工作进行调研。海外教育学院院长叶卫华、国际合作与交流处副处长刘小兵以及相关工作人员陪同参会。', '<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/4e74a15cba.png\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/eb4334e74a.png\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>　　4月18至20日，副校长刘小丽率队参加在南京市举办的第十届EGPC全球合作伙伴会议和2021来华留学高层论坛，并在会后赴河海大学、南京信息工程大学就国际化各项工作进行调研。海外教育学院院长叶卫华、国际合作与交流处副处长刘小兵以及相关工作人员陪同参会。</p>\n<p>　　第十届EGPC全球合作伙伴会议是以促进与&ldquo;一带一路&rdquo;沿线国家和地区文化、教育交流与合作，助力国家&ldquo;留学中国&rdquo;计划为目的，切实推进来华留学提质增效，搭建中国高校与海外大学、中学及当地教育机构双向合作平台。参加此次会议的有上海交通大学、中国石油大学（华东）、同济大学、东南大学、上海大学等国内135所高校，还有近百个国际合作代理机构或其驻华代表以及匈牙利大学代表团和东盟大学代表团等国外高校代表团。</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/d22c887433.jpg\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/1256202693.jpg\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>　　会议期间，刘小丽一行先后与教育部留学服务中心、中国教育国际交流协会来华留学分委会等单位人员，哈萨克斯坦共和国驻上海官员，印度尼西亚驻华大使馆人员等进行了深入交流。在大会院校代表发言环节中，叶卫华作了题为&ldquo;规范管理，提质增效，打造&lsquo;留学江财&rsquo;品牌&rdquo;的主旨发言，得到了与会代表的热烈反响。</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/0e0fe4e18d.jpg\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/22d3bb573c.jpg\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p>　　会后，刘小丽到兄弟高校就中外合作办学项目申报、师生出国（境）交流及研究生层次国际联合培养、来华留学生教育的管理体制、来华留学生招生录取与奖学金政策、全英语授课专业等方面进行调研。</p>\n<p>　　刘小丽强调，要利用好疫情带来的&ldquo;时间差&rdquo;找差距、补短板、强弱项、夯基础，多学习省内外兄弟高校的先进经验，进一步贯彻落实学校&ldquo;十四五&rdquo;期间来华留学生发展规划，提升招生与培养质量，创新管理手段，为疫情开放后的主动出击打下坚实基础。</p>\n<p style=\"text-align: right;\">　　（图文/海外教育学院&nbsp; 王琦&nbsp; 编辑/姜莹&nbsp; 刘家昕&nbsp; 审核/赵旻）</p>', 1, '江西财经大学新闻网', '江西财经大学新闻网', 5, 0, NULL, 1, '2021-04-24 17:48:38', 0, 0, NULL, NULL, 1, '2021-04-24 17:34:05', '2021-04-24 17:34:07', '谢志朋、', NULL, 1, '2021-04-24 17:34:13', NULL, NULL, 1, '2021-04-24 17:34:13', 1, '2021-04-24 17:48:38', '2021-04-24 17:48:38', 313, 'http://news.jxufe.edu.cn/uploadfile/82/Attachment/4e74a15cba.png', 0, NULL);
INSERT INTO `news` VALUES (80, '校领导到会计学院调研就业工作', '4月18至20日，副校长刘小丽率队参加在南京市举办的第十届EGPC全球合作伙伴会议和2021来华留学高层论坛，并在会后赴河海大学、南京信息工程大学就国际化各项工作进行调研。海外教育学院院长叶卫华、国际合作与交流处副处长刘小兵以及相关工作人员陪同参会。', '<div id=\"zoom\">\n<p style=\"text-align: center;\"><img src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/4e74a15cba.png\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/eb4334e74a.png\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>　　4月18至20日，副校长刘小丽率队参加在南京市举办的第十届EGPC全球合作伙伴会议和2021来华留学高层论坛，并在会后赴河海大学、南京信息工程大学就国际化各项工作进行调研。海外教育学院院长叶卫华、国际合作与交流处副处长刘小兵以及相关工作人员陪同参会。</p>\n<p>　　第十届EGPC全球合作伙伴会议是以促进与&ldquo;一带一路&rdquo;沿线国家和地区文化、教育交流与合作，助力国家&ldquo;留学中国&rdquo;计划为目的，切实推进来华留学提质增效，搭建中国高校与海外大学、中学及当地教育机构双向合作平台。参加此次会议的有上海交通大学、中国石油大学（华东）、同济大学、东南大学、上海大学等国内135所高校，还有近百个国际合作代理机构或其驻华代表以及匈牙利大学代表团和东盟大学代表团等国外高校代表团。</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/d22c887433.jpg\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/1256202693.jpg\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>　　会议期间，刘小丽一行先后与教育部留学服务中心、中国教育国际交流协会来华留学分委会等单位人员，哈萨克斯坦共和国驻上海官员，印度尼西亚驻华大使馆人员等进行了深入交流。在大会院校代表发言环节中，叶卫华作了题为&ldquo;规范管理，提质增效，打造&lsquo;留学江财&rsquo;品牌&rdquo;的主旨发言，得到了与会代表的热烈反响。</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/0e0fe4e18d.jpg\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/22d3bb573c.jpg\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p>　　会后，刘小丽到兄弟高校就中外合作办学项目申报、师生出国（境）交流及研究生层次国际联合培养、来华留学生教育的管理体制、来华留学生招生录取与奖学金政策、全英语授课专业等方面进行调研。</p>\n<p>　　刘小丽强调，要利用好疫情带来的&ldquo;时间差&rdquo;找差距、补短板、强弱项、夯基础，多学习省内外兄弟高校的先进经验，进一步贯彻落实学校&ldquo;十四五&rdquo;期间来华留学生发展规划，提升招生与培养质量，创新管理手段，为疫情开放后的主动出击打下坚实基础。</p>\n<p style=\"text-align: right;\">　　（图文/海外教育学院&nbsp; 王琦&nbsp; 编辑/姜莹&nbsp; 刘家昕&nbsp; 审核/赵旻）</p>\n<p>&nbsp;</p>\n</div>', 1, '江西财经大学新闻网', '江西财经大学新闻网', 5, 0, NULL, 1, '2021-04-24 17:39:52', 0, 0, NULL, NULL, 1, '2021-04-24 17:36:05', '2021-04-24 17:36:07', '谢志朋、', NULL, 1, '2021-04-24 17:39:17', NULL, NULL, 1, '2021-04-24 17:39:17', 1, '2021-04-24 17:39:52', '2021-04-24 17:39:00', 1291, 'http://news.jxufe.edu.cn/uploadfile/82/Attachment/4e74a15cba.png', 0, NULL);
INSERT INTO `news` VALUES (81, '宣传部文明办党支部开展党史学习教育专题学习会暨四月主题党日活动', '4月22日上午，宣传部文明办党支部开展党史学习教育专题学习会暨四月主题党日活动，校党委副书记朱小理以普通党员身份参加支部活动。会议由党支部书记韩国玉主持。', '<div id=\"zoom\">\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/2cdac9b27d.jpg\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>　　</p>\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;4月22日上午，宣传部文明办党支部开展党史学习教育专题学习会暨四月主题党日活动，校党委副书记朱小理以普通党员身份参加支部活动。会议由党支部书记韩国玉主持。</p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/c90efb5ba3.jpg\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p>　　朱小理对支部党史学习教育专题宣讲给予肯定，他指出，本次宣讲切入点非常好，既从总体上阐明了新民主主义革命时期党的光辉历程，又通过一个个鲜活的故事和典型的事例解读了共产党人践行初心和使命的坚定信仰信念，听后深受触动、深受启发。</p>\n<p>　　朱小理从&ldquo;学党史、悟思想、办实事、开新局&rdquo;的高度，对支部党员和支部工作提出了四点要求。一是抓好主业当好主人翁，在校党委的领导下履行好党史学习教育牵头部门职责，与各工作组、各级党组织紧密协调，推动党史学习教育务求实效，走在前列；二是聚焦中心贡献宣传力量，以立德树人为根本任务，全面宣传展示学校人才培养、科学研究、学科建设、就业创业等各项事业的突出成效；三是紧盯文明创建彰显工作成效，按照文明校园&ldquo;六个好&rdquo;标准，全面推进《文明校园建设行动计划》，对标测评细则，全力做好迎检工作；四是常抓不懈牢牢守住意识形态底线，意识形态工作是一项极端重要的工作，要扛起责任，落实落细，坚决守牢意识形态阵地。</p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/e4cbd5087b.jpg\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p>　　支部成员刘晓泉作&ldquo;壮丽篇章：中国新民主主义革命历程&rdquo;党史专题宣讲。刘晓泉从&ldquo;为什么要建党：为了初心和使命&rdquo;和&ldquo;为什么能成功：践行初心和使命&rdquo;两方面，深入阐述了践行初心、牢记使命、一心为民的中国共产党光辉历史。</p>\n<p>　　全体党员围绕&ldquo;重温历史，牢记使命&rdquo;主题开展主题党日学习。会上传达学习了《江西财经大学2021年全面从严治党工作会议》、习近平总书记在中央党校（国家行政学院）中青年干部培训班开班式上发表重要讲话精神、习近平总书记在福建考察期间重要讲话精神、《中国共产党组织处理规定（试行）》和党史学习教育领导小组《关于〈&ldquo;我为群众办实事&rdquo;实践活动工作方案〉的通知》；领学了《扎扎实实抓好党员干部党史学习教育》《中国共产党发展党员工作细则》和习近平总书记关于新发展阶段、新发展理念、新发展格局重要论述摘编。全体党员结合工作实际交流了个人学习体会。（文/徐丰&nbsp; &nbsp; 图/孙坚&nbsp; &nbsp; 编辑/姜莹&nbsp; &nbsp; 审核/赵旻）</p>\n</div>', 1, '江西财经大学新闻网', '江西财经大学新闻网', 5, 0, NULL, 1, '2021-04-24 17:41:02', 0, 0, NULL, NULL, 1, '2021-04-24 17:37:18', '2021-04-24 17:37:20', '谢志朋、', NULL, 1, '2021-04-24 17:39:14', NULL, NULL, 1, '2021-04-24 17:39:14', 1, '2021-04-24 17:41:02', '2021-04-24 17:40:00', 1313, 'http://news.jxufe.edu.cn/uploadfile/82/Attachment/2cdac9b27d.jpg', 0, NULL);
INSERT INTO `news` VALUES (82, '软件学院“世界法律日”宣传活动顺利结束', '值此4.22“世界法律日”到来之际，为提高广大学生的法律素养，增强学法、懂法、守法、用法的观念，提升参与法治社会建设的本领，软件与物联网工程学院青年志愿者协会举办世界法律日宣传活动，鼓励同学们学会利用法律维护自己的权益。', '<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;值此4.22&ldquo;世界法律日&rdquo;到来之际，为提高广大学生的法律素养，增强学法、懂法、守法、用法的观念，提升参与法治社会建设的本领，软件与物联网工程学院青年志愿者协会举办世界法律日宣传活动，鼓励同学们学会利用法律维护自己的权益。</p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/147e52bb56.png\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p>　　活动于4月22日中午12点开始，以摆点的方式在麦庐园校区一食堂门口进行。此次宣传活动包括两块内容：法律知识问答和&ldquo;你添我加&rdquo;互动小游戏。活动过程中，不少同学在法律知识问答的环节对自己的法律知识储备胸有成竹，答题迅速，毫不犹豫，表现得游刃有余。</p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/7035314dbd.png\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p>　　&ldquo;你添我加&rdquo;互动小游戏开始后，同学们尽情打开脑洞，发挥了天马行空的想象力，不断续写出一篇篇生动有趣的法律故事，寓教于乐，生动的故事情节带动了同学们的思绪，更加强了同学们对相关法律知识的掌握。</p>\n<p>&nbsp;</p>\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://news.jxufe.edu.cn/uploadfile/82/Attachment/4bd86e004b.png\" data-layer=\"photo\" data-bd-imgshare-binded=\"1\" /></p>\n<p>&nbsp;</p>\n<p>　　此次&ldquo;世界法律日&rdquo;宣传活动，不仅丰富了同学们的法律知识，提高了同学们的法律修养，更是激发了同学们学习法律知识的热情，增进了同学们与法律之间的亲近感。（图文/软件与物联网工程学院 毕俊如&nbsp; &nbsp; 编辑/毕俊如&nbsp; 审核/姜莹）</p>', 1, '江西财经大学新闻网', '江西财经大学新闻网', 5, 0, NULL, 1, '2021-04-24 17:41:43', 0, 0, NULL, NULL, 1, '2021-04-24 17:38:46', '2021-04-24 17:38:49', '谢志朋、', NULL, 1, '2021-04-24 17:39:11', NULL, NULL, 1, '2021-04-24 17:39:11', 1, '2021-04-24 17:41:43', '2021-04-24 17:41:00', 2131, 'http://news.jxufe.edu.cn/uploadfile/82/Attachment/147e52bb56.png', 0, NULL);

-- ----------------------------
-- Table structure for news_column
-- ----------------------------
DROP TABLE IF EXISTS `news_column`;
CREATE TABLE `news_column`  (
  `id` int(0) NOT NULL AUTO_INCREMENT,
  `title` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '新闻栏目名称',
  `description` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '对栏目的描述',
  `parent_id` int(0) NULL DEFAULT NULL COMMENT '父栏目id',
  `external_link` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '栏目链接。如果新闻栏目是其他网站，则可以设置栏目链接用于跳转',
  `is_has_children` tinyint(0) NOT NULL DEFAULT 0 COMMENT '是否有子栏目。该字段是为element-ui的树形表格设计的，当is_has_children为true时，就可以点击展开子栏目表格',
  `create_time` datetime(0) NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `unique_title`(`title`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 31 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '新闻栏目表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of news_column
-- ----------------------------
