-- ----------------------------
-- Table structure for news_column_settings
-- ----------------------------
DROP TABLE IF EXISTS `news_column_settings`;
CREATE TABLE `news_column_settings`  (
  `id` int(0) NOT NULL AUTO_INCREMENT,
  `col_id` int(0) NOT NULL COMMENT '外键，对应的新闻栏目id',
  `enabled` tinyint(0) NOT NULL DEFAULT 1 COMMENT '是否开启该栏目;0-false,1-true',
  `menu_order` tinyint(0) NOT NULL DEFAULT 127 COMMENT '菜单显示顺序，默认有个最大值，用于排序',
  `module_order` tinyint(0) NULL DEFAULT NULL COMMENT '模块序号，决定在首页哪个模块上显示该栏目新闻列表，为null则不在首页上显示新闻列表',
  `show_img_on_the_right` tinyint(0) NOT NULL DEFAULT 0 COMMENT '用于设置栏目列表的新闻图片是否在右边显示；默认false，左边显示图片',
  `create_time` datetime(0) NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `update_time` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0),
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `col_id`(`col_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 23 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '新闻栏目设置表，为了将栏目设置信息和栏目基本信息解耦开' ROW_FORMAT = Dynamic;

